css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("jensweinreich.de") {


body {
	background: url(http://upload.wikimedia.org/wikipedia/commons/6/68/South_Africa_provinces_%2B_flag_back.png) repeat-y fixed  center top DarkKhaki !important;
	/* 
	The BACKGROUND-IMAGE has been created by Taifarious1 <http://commons.wikimedia.org/wiki/User:Taifarious1> 
	and published under a CC-by-sa 3.0 licence <http://creativecommons.org/licenses/by-sa/3.0/deed.en>
	*/
}


/*
 * HEADER
 */

#header {
	background: none repeat scroll 0 0 OliveDrab !important;
	border-bottom: 15px double #333 !important;
	-moz-box-shadow: 0 2px 1px #333 !important;
	-webkit-box-shadow: 0 2px 1px #333 !important;
	box-shadow: 0 2px 1px #333 !important;
	opacity: 0.8 !important;
}

/* H-LEFT */
.headerleft, .headerleft a, .headerleft a:visited, .headerleft a:active {
	text-shadow:1px 1px 1px #333 !important;
}
.headerleft a:hover {
	color: DarkKhaki !important;
}

/* H-RIGHT */
.headerright a:hover {
	background-color: DarkOliveGreen !important;
	color: #FFF !important;
	text-shadow: 0px 0px 5px #FF0 !important;
}


/*
 * CONTENT
 */
 
#content {
	background-color: rgba(255, 255, 255, 0.85) !important;
}
.postinfo:hover {
	background-color: rgba(240,240,240,0.6) !important;
}
#l_sidebar {
	border-top: 0 !important;
	background-color: rgba(164,164,164,0.5) !important;
}
#l_sidebar ul li a:hover {
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}

/*
 * FOOTER
 */

#footer {
	background:none repeat scroll 0 0 darkkhaki !important;
	border-top:5px solid darkgoldenRod !important;
}
#footer, #footer p, #footer a, #footer a:hover, #footer a:visited {
	color: #333 !important;
}

}
