css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("insanejournal.com") {
 
body.sitescheme > div > div { width:150px !important; overflow:visible !important;  }

table.menu, 
table.head tbody, 
table.head form, 
table.menu tbody {font-family: Verdana,Tahoma,Geneva,sans-serif !important; background-color: #f2f2f2 !important; } 

body.sitescheme, 
.sitescheme table, 
#Content form { background: #ffffff !important; color: #000000 !important; font: 12px Verdana, Tahoma, Geneva, sans-serif !important; } 

.sitescheme a, .sitescheme a:visited, .sitescheme a:active { color: #000099 !important; text-decoration: none !important; } 

.sitescheme a:hover { text-decoration: underline !important; } 

.sitescheme table.nav { border: ridge 1px #000000 !important; } 

.sitescheme a.nav:visited { color: #000000 !important; } 

.sitescheme a.nav:hover { color: #000099 !important; } 

.sitescheme td.head { color: #ffffff !important; background-color: #99ccff !important; font: 10px Verdana, Tahoma, Geneva, Arial, sans-serif !important; } 

.sitescheme table.banner {margin-top:-152px !important; visibility:hidden !important;} 

table.menu, #menubar {background-color:#f5c577 !important;} 

table.head tbody tr td table tbody tr td table { background-color: #f2f2f2 !important; }
}
