css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nokiagate.com") {

/* lists */
#header {
height:160px !important;
}
#content-in {
min-width:1250px;
}
.tborder[cellspacing="$stylevar[cellspacing]"] {
display:none;
}
table + #threadslist td {
width:100% !important;
min-width:1103px;
min-height:111px;
display:block;
text-align:right !important;
float:left;
border-bottom:1px black solid !important;
padding-bottom:7px;
padding-top:7px;
padding-right:0px;
}
table + #threadslist .tcat:hover {
background-color:#666 !important;
}
table + #threadslist .alt2:hover, table + #threadslist .alt1:hover {
background-color:#D9D9D9 !important;
}
table + #threadslist td img[style="height: 200px; width: 200px;"] {
width:110px !important;
height:110px !important;
float:right;
margin-right:7px;
}
table + #threadslist td img[style="height: 200px; width: 200px;"]:hover {
position:absolute !important;
width:200px !important;
height:200px !important;
border: groove 5px white !important;
}
table + #threadslist td div {
float:left;
width:89%;
text-align:right;
margin-right:7px;
}
table + #threadslist td div h3 {
margin-bottom:5px;
margin-top:5px;
float:right;
}
table + #threadslist td div span[style="color: rgb(255, 0, 0);"], #threadslist td div span[style="color: red;"] {
float:left;
margin-left:10px;
}
table + #threadslist td.thead {
height:10px !important;
min-height:0px !important;
width:1202px !important;
}
img[src="http://images.nokiagate.com/styles/bingo/misc/sc.gif"]{
display:none;
}
.tborder + br + center {
display:none;
}
#souq_menu + h1[style="text-align: center;"] span {
display:none;
}
/* discussion*/
tbody td + td + td[valign="top"] {
height:100px;
overflow:hidden;
display:block
}
tbody td + td + td[valign="top"]:hover {
height:auto !important;
}
/* top bar */

.tborder tbody tr[style="text-align: center;"] .alt1:hover {
background-color:#D9D9D9 !important;
}
}
