css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("jensweinreich.de") {


body {
	background: url(http://img809.imageshack.us/img809/3528/kunstrasen.jpg) right top transparent;
}


/*
 * HEADER
 */

#header {
	background-color: rgba(0,0,0,0.2) !important;
	border: 8px solid rgba(255,255,255,0.75) !important;
	border-top: 0 !important;

	margin: 0 auto 25px !important;
	padding: 0 0 5px !important;

	-moz-box-shadow: 0 0 5px #fff !important;
	-webkit-box-shadow: 0 0 5px #fff !important;
	box-shadow: 0 0 5px #fff !important;
	
	-moz-border-radius: 0 0 3px 3px !important;
	-webkit-border-radius: 0 0 3px 3px !important;
	border-radius: 0 0 3px 3px !important;
	
	opacity: 1 !important;
}

/* H-LEFT */
.headerleft, .headerleft a, .headerleft a:visited, .headerleft a:active {
	text-shadow: 1px 1px 4px #333 !important;
}

/* H-RIGHT */
.headerright a:hover {
	background-color: rgba(255,255,255,0.5) !important;
	color: #333 !important;
	text-shadow: 0px 0px 3px DarkOliveGreen !important;

	-moz-box-shadow: 0 0 5px #fff !important;
	-webkit-box-shadow: 0 0 5px #fff !important;
	box-shadow: 0 0 5px #fff !important;
}


/*
 * CONTENT
 */
 
#content {
	background-color: rgba(255, 255, 255, 0.8) !important;
}
.postinfo:hover {
	background-color: rgba(240,240,240,0.6) !important;
}
#l_sidebar {
	border-top: 0 !important;
	background-color: rgba(164,164,164,0.5) !important;
}
#l_sidebar ul li a:hover {
	-moz-box-shadow: none !important;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}

/*
 * FOOTER
 */

#footer {
	background:none rgba(0,0,0,0.5)!important;
	border-top:5px solid rgba(255,255,255,0.75) !important;
}
#footer, #footer p, #footer a, #footer a:hover, #footer a:visited {
	color: #fff !important;
}

}
