@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* 161: like a page
 * 21: joinned a group
 * 8: is now friends with
 * 38: attending event
 * 283: like a page on a site
 */

li[data-ft*=",\"sty\":\"161\","],
li[data-ft*=",\"sty\":\"283\","],
li[data-ft*=",\"sty\":161,"],
li[data-ft*=",\"sty\":283,"] {
  background-color: #FDFDFF !important;
}

li[data-ft*=",\"sty\":\"21\","],
li[data-ft*=",\"sty\":21,"] {
  background-color: #FFFFFD !important;
}

li[data-ft*=",\"sty\":\"8\","],
li[data-ft*=",\"sty\":8,"] {
  background-color: #FDFFFD !important;
}

li[data-ft*=",\"sty\":\"38\","],
li[data-ft*=",\"sty\":38,"] {
  background-color: #FFFDFD !important;
}

li[data-ft*=",\"sty\":\"161\","],
li[data-ft*=",\"sty\":\"283\","],
li[data-ft*=",\"sty\":\"21\","],
li[data-ft*=",\"sty\":\"38\","],
li[data-ft*=",\"sty\":\"8\","],
li[data-ft*=",\"sty\":161,"],
li[data-ft*=",\"sty\":283,"],
li[data-ft*=",\"sty\":21,"],
li[data-ft*=",\"sty\":38,"],
li[data-ft*=",\"sty\":8,"] {
  padding-top: 3px !important;
  padding-bottom: 3px !important;
  font-size: 8pt !important;
  opacity: .75;
}

li[data-ft*=",\"sty\":\"161\","]:hover,
li[data-ft*=",\"sty\":\"283\","]:hover,
li[data-ft*=",\"sty\":\"21\","]:hover,
li[data-ft*=",\"sty\":\"38\","]:hover,
li[data-ft*=",\"sty\":\"8\","]:hover,
li[data-ft*=",\"sty\":161,"]:hover,
li[data-ft*=",\"sty\":283,"]:hover,
li[data-ft*=",\"sty\":21,"]:hover,
li[data-ft*=",\"sty\":38,"]:hover,
li[data-ft*=",\"sty\":8,"]:hover {
  opacity: 1;
}

li[data-ft*=",\"sty\":\"161\","] .uiStreamMessage,
li[data-ft*=",\"sty\":\"283\","] .uiStreamMessage,
li[data-ft*=",\"sty\":\"21\","] .uiStreamMessage,
li[data-ft*=",\"sty\":\"38\","] .uiStreamMessage,
li[data-ft*=",\"sty\":\"8\","] .uiStreamMessage,
li[data-ft*=",\"sty\":161,"] .uiStreamMessage,
li[data-ft*=",\"sty\":283,"] .uiStreamMessage,
li[data-ft*=",\"sty\":21,"] .uiStreamMessage,
li[data-ft*=",\"sty\":38,"] .uiStreamMessage,
li[data-ft*=",\"sty\":8,"] .uiStreamMessage {
  font-size: 8pt !important;
  margin-bottom: 2px !important;
}

li[data-ft*=",\"sty\":\"161\","] .profilePic,
li[data-ft*=",\"sty\":\"283\","] .profilePic,
li[data-ft*=",\"sty\":\"21\","] .profilePic,
li[data-ft*=",\"sty\":\"38\","] .profilePic,
li[data-ft*=",\"sty\":\"8\","] .profilePic,
li[data-ft*=",\"sty\":161,"] .profilePic,
li[data-ft*=",\"sty\":283,"] .profilePic,
li[data-ft*=",\"sty\":21,"] .profilePic,
li[data-ft*=",\"sty\":38,"] .profilePic,
li[data-ft*=",\"sty\":8,"] .profilePic {
  width: 25px !important;
  height: 25px !important;
  margin-left: 25px !important;
  margin-top: 2px !important;
}

}
