@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joystiq.com") {

/* backgrounds */
body.perm, body.main, body[class=" "], body[class=""] { background: url('http://www.blogsmithmedia.com/www.joystiq.com/media/pixelation-bg.gif') !important; } 


/* Image replacements */

#scan-lines { background: url("data:image/png;base64,") !important; }

#mp-footer #mp-paginate li.prev a { background: url("data:image/png;base64,") 0 0 no-repeat !important; position: absolute !important; left: 13px !important; }

#mp-footer #mp-paginate li.next a {
background: url("data:image/png;base64,") 0 0 no-repeat !important; position: absolute !important; left: 476px !important; }

.galleria-image-nav-left, .galleria-image-nav-right{ background-image: url("data:image/png;base64,") !important; background-repeat: no-repeat; }



/* Top area width/positioning fixes */

#main-nav { width: 970px !important; position: relative !important; left: 50% !important; margin-left: -485px !important; }
#nav { width: 960px !important; }

#mc-outer, #main-carousel { width: 970px !important; position: relative !important; left: 50% !important; margin-left: -485px !important;  }



/* padding fixes */

#branding { padding: 0px 10px !important; }

#container { padding: 5px 10px 10px !important; position: relative !important; top: -10px !important; }

#grid { padding: 0px 10px !important; }

.post img { padding-right: 10px !important; }


/* Carousel/Cycling */

.caption { background: #EEE !important; text-align: center !important; -moz-border-radius: 0px 0px 5px 5px !important; height: 22px !important; }
.caption.active { background: #BDF !important; }
.caption .text { color: #999 !important; text-align: center !important; position: relative; top: -5px !important; }
.caption.active .text { color: #000 !important; }
#mc-outer, #main-carousel { background: white !important; }


/* Opacity fixes */

#nav #sec-nav ul li a { opacity: .85 !important; }
#nav #sec-nav ul li a:hover { opacity: 1 !important; }



.post .post-inner .post-body img { max-width: 535px !important; }
.post-body li { position: relative !important; left: 10px !important; }
}
