css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("serienjunkies.org") {

/* Theme Name: Serienjunkies.org - Some Fixes */

/* SeitenrÃ¤nder (Links und Rechts) entfernen. */
#rap {
  position: relative;
  width: 98% !important;
  margin: 0 !important;
  border: none !important;
}

#header {background:url("http://justpic.info/images/4b65/big.jpg") no-repeat top left transparent !important;}
#content {width:70% !important;}

/* Navigation (Hauptnavigation) umgestalten */
#gnav { position: absolute; right: 0; }
#gnav li:first-child{ display: none !important;}
#gnav li { margin: 0 !important; padding: 0 !important;}
#gnav li a {
  display: block !important;
  text-align: center;
  width: 150px;
  background: #cdcdcd;
  margin: 5px 10px 0 0;
  text-decoration: none;
  border: 1px solid #3d3d3d;
}

#gnav li.current_page_item a:link, 
#gnav li.current_page_item a:visited, 
#gnav li.current_page_item a:hover, 
#gnav li.current_page_item a:active, 
#gnav li a:hover, #gnav li a:active
{
 border-bottom: 1px solid #3d3d3d !important;
 background: #dfdfdf !important;
}

/* Serienkalender (Unterhalb des Banners) entfernen. */
#calhead {display: none;}

/* Dokujunkies-Badge (Ecke Rechtsoben) entfernen. */
#pBadge {display: none !important}
}
