css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/*normal*/
.gmbutton2, .smbutton{background:-moz-linear-gradient(-90deg, #F1F4F1, #D4E0D3) repeat scroll 0 0 transparent!important;-moz-border-radius:5px!important; border: 1px solid #AEC2AA!important;}

.gmbutton2:hover, .smbutton:hover{background:-moz-linear-gradient(-90deg, #F9FeF9, #E6EDE4) repeat scroll 0 0 transparent!important;}

.gmbutton2:active, .smbutton:active{background:-moz-linear-gradient(-90deg, #D4E0D3, #eeF0ee) repeat scroll 0 0 transparent!important;}

/*green*/
.gmbutton2g{background:-moz-linear-gradient(-90deg, #c3f36d, #a3d33d) repeat scroll 0 0 transparent!important;}

.gmbutton2g:hover{background:-moz-linear-gradient(-90deg, #e3ff8d, #c3f36d) repeat scroll 0 0 transparent!important;}

.gmbutton2g:active{background:-moz-linear-gradient(-90deg, #a3d33d, #c3f36d) repeat scroll 0 0 transparent!important;}

/*red*/
.gmbutton2r{background:-moz-linear-gradient(-90deg, #e85877, #bc2142) repeat scroll 0 0 transparent!important;}

.gmbutton2r:hover{background:-moz-linear-gradient(-90deg, #f889a1, #e85877) repeat scroll 0 0 transparent!important;}

.gmbutton2r:active{background:-moz-linear-gradient(-90deg, #bc2142, #e85877) repeat scroll 0 0 transparent!important;}

/*bb*/
.bb .gmbutton2{background:transparent none!important;border-width:0!important;}

/*navlinks*/

.nav2 .shadow a{background:-moz-linear-gradient(-90deg, #F1F4F1, #D4E0D3) repeat scroll 0 0 transparent!important;-moz-border-radius:5px!important; border: 1px solid #AEC2AA!important;}


.nav2 .shadow a:hover{background:-moz-linear-gradient(-90deg, #F9FeF9, #E6EDE4) repeat scroll 0 0 transparent!important;}


.nav2 .shadow a:active{background:-moz-linear-gradient(-90deg, #D4E0D3, #eeF0ee) repeat scroll 0 0 transparent!important;}

.nav2 .shadow strong{background:-moz-linear-gradient(-90deg, #F9FeF9, #E6EDE4) repeat scroll 0 0 transparent!important;-moz-border-radius:5px!important; border: 1px solid #AEC2AA!important;}



.gmbutton2 b{display:none!important;}

#whichFileAltalt div:nth-of-type(3), #form3 div:nth-child(5){background:-moz-linear-gradient(-90deg, #c3f36d, #a3d33d) repeat scroll 0 0 transparent!important;-moz-border-radius:5px;border: 1px solid #AEC2AA!important;color:#101010!important;}

#whichFileAltalt div:nth-of-type(3) *{color:#101010!important;}

#whichFileAltalt div:nth-of-type(3).deckItemLabel{background:transparent none!important; border-width:0!important;}
}
