css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {



#dashboard_index{background:#24708E url(http://i842.photobucket.com/albums/zz343/Waaytoocheezy/Wooden_Clouds__Wallpaper_Tile__by_s.jpg) repeat !important;}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#296B7C!important;}



ol#posts li.notification{background-color: #456179 !important;

border-bottom: 1px solid #4C97AB !important;

color: #A8BCCB !important;}



#left_column .dashboard_options_form {

   background: url('http://assets.tumblr.com/images/white_20.png')#3182A5;

}



#right_column a#dashboard_switch_blog_menu_current:hover {

   background: #3D818E;

}



#nav .nav_item.active{

   background-color:#2D6779

}



#left_column #posts .post .permalink{

   background-color:#377C9E;

}



#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{

   background-color:#4380A1;

}



}
