css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("connotea.org") {
body {
font-size: 80% !important;
}
#constrain .constrain {
background: none !important;
max-width:100% !important;
}
#content{
background: none !important;
}
#wikiinfo{
display: none !important;
}
#loginaccount-box .login-buttons {
background: none !important;
}
#loginaccount-box h2 {
background: #bbb !important;
}
h3.sectiontitle{
border: 1px solid #abb !important;
color: #fff !important;
}
a{
text-decoration:none !important;
font-weight:normal !important;
}
.icons {
border: 1px solid #ddd !important;
margin: 0 !important;
float:right !important;
background-color:#eee !important;
padding: 0 0.25em !important;

}
.citationline{
color: #444 !important;
font-size: 90% !important;
}
.content-mybookmark{
border-bottom: 2px dotted #eee !important;
line-height:1.4em !important;
padding:1em 0 !important;
margin-left:1em !important;
}
.content-mybookmark > a{
margin-left:-1em !important;
font-weight: bold !important;
}
.truetitle{
margin:1em !important;
background-color:#f6f6f6 !important;
padding: 0.25em !important;
}
#banner .title {
display: none !important;
}
#banner {
margin: 0 !important;
max-width: 100% !important;
padding: 0 !important;
position: relative !important;
}
#nav-wrap,
#library-menu{
float:left !important;
}
#nav-wrap{
width: 100% !important;
}
#nav{
max-width: 10000px !important;
}
ul#library-menu li a{
float:none !important;
}
#mybookmark-title{
line-height: 1em !important;
background-color: #8a9 !important;
}
#constrain{
padding: 0 !important;
}
#main-menu,
#footer-wrap,
#sidetags_cabinet,
#toolbox,
#reportproblembox{
display: none !important;
}
#loginaccount-box h2 {
display: none !important;
}
#mybookmark-title{
background: none !important;
}
#mybookmarktitle{
color:#000 !important;
}
#sidetags_cabinet,
#sideusers_cabinet,
#toolbox,
#sidetags_related,
#sideusers_related
{
background-color: #F6F6F6 !important;
border:1px dotted #DDDDDD !important;
}
#searchbar,
#searchbar .searchlink{
background:none !important;
}
#footmenu-wrap{
background: #eee !important;
}
.helpicon{
display:none !important;
}
#number {
color: #fff;
}
a.postedtag{
background-color:#F6F6F6 !important;
border:1px solid #eee !important;
padding:0pt 0.25em !important;
}
.posted {
color: #fff !important;
font-size: 90% !important;
}

a.proxit_link,
#rhsads {
display:none !important;
}

#head-signup, #bottom-wrap, .fivereasons-title {
display:none;
}
}
