css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/')

 , url-prefix("https://www.tumblr.com/") {

  #tip{

    display: none;

  }

  #footer {

    display:none !important; 

    visibility: hidden !important;

  }

  #container {

    width: 900px !important;

    background-color:#000000

  }

  div.l-header {

    background-color:#000000 !important;width:auto !important

  }

  body {

    background:#000000 !important;

  }

  #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background:#141414 !important;

  }

  #right_column #dashboard_switch_blog_arrow_current{

    display:none !important;

  }

  #right_column #dashboard_controls_radar_media {

    background:none !important;

  }

  #right_column #dashboard_controls_radar_media{

    color:#FFFF4D !important;

  }

  #right_column #dashboard_controls_radar_media a{

    color:#FFFF4D !important;

  }

}
