css:
body {
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
	font-size:10pt;
	background:#1c1c1c url('http://i50.tinypic.com/o8ifx4.jpg') top left fixed;
	color:#757575;
	padding-left:5px;
	padding-right:5px;
	margin-right:0;
	margin-left:0;
	margin-top:5px;
}

td {
	font-size:10pt;
	padding:0;
	margin:0;
}

td.reply {
	border: none;
}

blockquote {
	font-size:10pt;
}

.doubledash {
	color:#757575;
}

.inputtext {
	margin:0;
	margin-left: 4px;
	margin-right:2px;
	padding:1px 4px;
	border: none;
	outline:none;
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
	font-size:10pt;
	background: #262626;
}

input.inputtext {
	height:1.75em;
}

td>.inputtext {
	height:auto;
	padding-top:2px;
	padding-bottom:3px;
}

.inputtext:focus {
	border:1px solid #000;
}

.logo {
	display: none;	
}

.logo img {
	border:1px solid #000;
}

.logo b span {
	font-family:tahoma;
}

.logo span {
	font-size:28px;
	letter-spacing:-2px;
}

hr {
	border:none;
	border-top:3px solid #262626;
	height:0;
}

.postblock {
	border: none;
	font-size:10pt;
	padding:0 5px;
	line-height:20px;
}

.rules {
	font-size:10pt;
}

.rules a {
	text-decoration:none;
}

form a img {
	margin-top:3px;
}

iframe, .rotating {
	background:#00ff00;
	border-right:1px solid #D9BFB7;
	border-bottom:1px solid #D9BFB7;
}

.pages {
	border:none;
	background:#222222;
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
	-khtml-border-radius: 8px;
	-border-radius: 8px;
}

.pages td {
	border:none;
	padding:1px 5px;
}

.pages td {
	color:#3b3b3b;
}

.pages td b {
	color:#ff4900;
}

.deletebuttons {
	text-align:right;
}

.deletebuttons br {
	display:none;
}

a, a:visited {
	color:#757575;
}

a:hover {
	color:#ff4900;
}

a.quotelink {
	color:#757575;
}

.logo {
	clear:both;
	text-align:center;
	font-size:24pt;
	color:#757575;
	width:100%;
}

form {
	margin-top:0;
}

.rules {
	width:468px;
	font-size:10px;
}

.rules>li {
	list-style:none;
}

.rules>li:before {
	content:"\2022 \20";
}

.postblock {
	background:#262626;
	color:#757575;
	font-weight:800;
}

.footer {
	text-align:center;
	font-size:12px;
}

.unkfunc {
	color:#757575;
}

.filesize {
	text-decoration:none;
}

td .filesize {
	display:inline;
	background:none;
}

.filesize span, span.postername, span.filetitle, span.commentpostername {
	unicode-bidi:embed;
}

.filetitle, .replytitle {
	background:inherit;
	color:#fff;
	font-weight:800;
}

.postername, .commentpostername {
	background:inherit;
	color:#757575;
	font-weight:800;
}

.postertrip {
	background:inherit;
	color:#fff;
}

.oldpost {
	background:inherit;
	color:#00ff00;
	font-weight:800;
}

.omittedposts, .abbr {
	color:#757575;
}

.reply {
	background:#262626;
	color:#757575;
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
	-khtml-border-radius: 8px;
	-border-radius: 8px;
}

.replyhl {
	background:#3e3e3e;
	color:#757575;
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
	-khtml-border-radius: 8px;
	-border-radius: 8px;
}

.doubledash {
	vertical-align:top;
	clear:both;
	float:left;
}

a.quotejs:active, a.quotejs:link, a.quotejs:visited {
	color:#757575;
	text-decoration:none;
}

a.quotejs:hover {
	color:#ff4900;
}

.tn_thread {
	width:200px;
	height:100px;
	margin:0 20px 20px 20px;
	float:left;
	background:#00ff00;
	border:#ea8 1px solid;
	text-align:center;
}

.tn_reply {
	width:100px;
	height:100px;
	margin:0 20px 20px 20px;
	float:left;
	background:#00ff00;
	border:#ea8 1px solid;
	text-align:center;
}

.adHeadline {
	font:bold 10pt serif;
	text-decoration:underline;
	color:#00ff00;
}

.adText {
	font:normal 10pt serif;
	text-decoration:none;
	color:#00ff00;
}

#ad {
	width:120px;
	margin:0;
	padding:0;
	position:absolute;right:150px;
	border:1px solid #00ff00;
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
	font-size:11px;
}

#ad div {
	margin:0;
	padding:.4em;
}

#ad div.ad-title {
	padding:0;
	background:#00ff00;
	color:#800000;
	font-size:11px;
}

#ad div.ad-title a {
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
	color:#00ff00;
}

#ad div.ad-text a {
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
}

.bottomAdTitle {
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
	background:#00ff00;
	color:#800000;
}

#bottomAd {
	height:52px;
	font-size:11px;
}

#bottomAdOuter {
	width:600px;
	border:1px solid #EA8;
	font-size:11px;
}

.spoiler a.quotelink, .spoiler .unkfunc {
	color:inherit;
}

.exif {
	display:none;
	font-family:arial,helvetica,sans-serif;
	font-size:xx-small;
}

.exif td {
	font-family:arial,helvetica,sans-serif;
	font-size:xx-small;
}

.exif td b {
	font-weight:800;
	text-decoration:underline;
	font-size:x-small;
}

#header {
	position:absolute;
	top:2px;
	left:5px;
	right:5px;
}

* html #header {
	width:100%;
}

#navtop, #navbot {
	left:0;
	float:left;
}

#navtopr, #navbotr {
	right:0;
	display:block;
	float:right;
	text-align:right;
}

#header, #navbot, #navbotr {
	font-size:9pt;
	color:#262626;
}

#header a, #navbot a, #navbotr a, .pages td a {
	font-weight:normal;
	padding:1px;
	text-decoration:none;
	color:#757575;
}

#header a:hover, #navbot a:hover, #navbotr a:hover, .pages td a:hover {
	color:#ff4900;
}

#footer {
	clear:both;
	padding-top:10px;
}

#footer center font {
	font-size:7pt;
}

.pages td a {
	color:#757575;
}

td.replyhl {
	border:1px solid #00ff00;
	border-left:none;
	border-top:none;
	padding:2px;
}

td.deletebuttons input.checkbox {
	margin:1px 2px 1px 2px;
	background: #262626;
}

div.logo img {
	margin:5px 0 5px 0;
}

.fstitle {
	float:left;
	width:25px;
}

input {
	background: #383838;
	color: #757575;
	border: none;
	padding-top: 2px;
	padding-bottom: 2px;
	padding-left: 10px;
	padding-right: 10px;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	-khtml-border-radius: 4px;
	-border-radius: 4px;
}
