css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') {




#nav .nav_item.active {
    background: rgba(0,0,0,0.1) !important;
  }


#dashboard_index{background: url('http://i221.photobucket.com/albums/dd107/kellivengeance6661/twins-4.jpg')bottom left  #f3f1f4 no-repeat fixed !important;}





#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background: rgba(0,0,0,0.5) !important;
  }





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#646464!important;}







ol#posts li.notification{background-color: #456179 !important;



border-bottom: 1px solid #4C97AB !important;



color: #A8BCCB !important;}







#left_column .dashboard_options_form {



   background: url('http://assets.tumblr.com/images/white_20.png')#514757;



}







#right_column a#dashboard_switch_blog_menu_current:hover {



   background: #514757;



}
#right_column #dashboard_switch_blog_arrow_current{

    display:none !important;

}









#nav .nav_item.active{



   background-color:#514757



}







#left_column #posts .post .permalink{



   background-color:#615569;



}







#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{



   background-color:#514757;



}







}
