css:
/*By InTheCatBoxAgain. Removes all the banner ads, hides the background image, and makes much better use of screen real estate.*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.adultswim.com") {





body#lia-body{background-color:#000000 !important; background-image:none !important; background:black!important; margin:0px !important;}

#navcss-000000{margin-top:0px !important; padding:0px !important;}
#long-top-ad, #long-bottom-ad, #legal-footer{ display:none !important;}


#lia-body .MinimumWidthContainer .lia-content{  padding:0px !important; width:99% !important; line-
height:12px !important;}

.lia-content .lia-component-common-widget-page-title, #lia-body .lia-content .lia-component-common-widget-page-title a{margin-bottom:0px!important; margin-top:15px !important; margin-left:5px !important; font-size:18px !important; color:white!important}

/*Green link color*/
.lia-body .lia-content a, .lia-body .lia-content a:link, .lia-body .lia-content a:visited, .lia-body .lia-content a:hover, .lia-body .lia-content a:active, .lia-body .lia-content a:focus{color:#ff3333}

.lia-quilt-row-user a{color:#FFF!important; font-weight:bold!important}
.lia-quilt-row-crumb a{color:#ff6633!important; font-weight:bold!important}
.newMessagesCountColumn a{color:#ff6633!important}
.lia-menu-bar a{color:white!important}
.lia-link-navigation .DateTime{color:white!important}
.lia-button-primary{border:solid #fff!important;background-color:black!important;color:white!important}
.lia-content ul.lia-paging-full .lia-paging-page-first > span.lia-link-disabled, #lia-body .lia-content ul.lia-paging-full .lia-paging-page-last > span.lia-link-disabled, #lia-body .lia-content ul.lia-paging-full .lia-paging-page-active > span.lia-link-disabled, #lia-body .lia-content ul.lia-paging-full .lia-paging-full-pages li > a:hover{background-color:black!important;border:1px solid white !important; color:white!important}
.lia-component-menu-bar a{color:white!important}
.BoardBrowserColumnTaplet a{color:white!important; font-weight:bold!important}
.ActionLinksTaplet a{color:white!important; font-weight:bold!important}

/*Menu Bars*/
.lia-quilt-row-user {padding-left:4px!important;padding-top:3px!important;padding-right:2px!important;background-color:#232323 !important;}
.lia-quilt-row-crumb{padding-left:2px!important;padding-right:2px!important;background-color:#424242!important}
.lia-content .lia-component-common-widget-breadcrumb{border-bottom:none!important; margin:0px 0px 0px!important}
.lia-content .lia-panel .lia-panel-heading-bar{border-bottom:none!important}
.lia-content .lia-menu-bar{background-color:#535353!important; padding-top:4px; padding-left:4px!important;}
.lia-content .lia-menu-bar .lia-menu-bar-buttons{margin:0px !important}
.lia-component-menu-bar{background-color:#333!important; padding-top:4px!important;padding-bottom:4px!important; padding-left:4px!important; border-bottom:6px solid #535353 !important}
.lia-component-menu-bar .lia-paging-full{margin:0px!important}

/*Board Tables*/
.lia-content table.lia-list-wide{margin:0px!important}
.lia-content table, .mceContentBody table{border-spacing:1px 1px!important; border-collapse:separate!important}
.lia-content table.lia-list-wide th{color:white!important; font-weight:strong!important; background-color:#424242!important}
.lia-content table.lia-list-wide tr.lia-list-row-float td{background-color:#333333!important}
.lia-row-odd, .lia-row-even{background-color:#232323!important; border:none!important}
.lia-content table.lia-list-wide td{border-bottom:0px solid #000 !important}
.lia-quilt-footer, .lia-quilt-admin-footer{border-top:none!important}

/*Messages*/
.lia-message-heading{background-color:#535353!important;padding-left:10px!important; padding-top:3px!important}
.lia-message-view{background-color:#232323!important; border-bottom:1px solid black!important}
.lia-content .message-list .first-message{padding-top:0px!important; border-top:1px solid black!important}
.lia-panel-message{margin:0px!important; border-bottom:none!important}
.lia-quilt-column-main-right .lia-quilt-column-alley-right, .lia-quilt-row-footer .lia-quilt-column-alley-inner{margin:0px!important; padding-left:0px!important; border-left:1px solid #000 !important}
.as-reply-message-id-wrapper, .lia-message-dates, .lia-component-body{padding-left:10px!important}
.lia-message-heading .lia-quilt-column-alley-right{border:none!important}
.lia-message-heading .lia-quilt-column-alley-right a{color:white!important}
.lia-message-footer{margin:0px!important}
.lia-message-author{padding-top:5px!important}
.lia-message-author, .lia-message-kudos, .lia-message-statistics{padding-left:10px!important}
.lia-button-image-kudos-give{background-color:#ff6633 !important;}
.lia-button-image-kudos-count{background:#ff906c !important;}
.lia-button-image-kudos-count .MessageKudosCount{color:black !important}
.MessageTags, .AddMessageTags{padding-left:10px!important; color:#828282!important}
.MessageTags a{color:white!important}
.AddMessageTags a{color:#ff6633!important;font-weight:bold!important}
.lia-message-actions .lia-button-group{margin:0px!important}


}
