/* body */
body {
	background: #CCD2D9 !important;
	font-family: Verdana, Helvetica, Ariel, sans-serif !important;
	font-size: 16px !important;
	font-style: normal !important;
	color: #566474 !important;
}

body.hp {
	background-color: #CCD2D9 !important;
}
body.toc {
	background-color: #CCD2D9 !important;
}
/* links */
a:link, a:visited {
	color: #20252B !important;
	text-decoration: none !important;
	border-bottom-width: thin !important;
	border-top-style: none !important;
	border-right-style: none !important;
	border-bottom-style: dotted !important;
	border-left-style: none !important;
	border-top-color: #FF6600 !important;
	border-right-color: #FF6600 !important;
	border-bottom-color: #FF6600 !important;
	border-left-color: #FF6600 !important;
}
a:hover,a:active {
	color: #FF6600 !important;
	text-decoration: none !important;
	border-bottom-width: thin !important;
	border-top-style: none !important;
	border-right-style: none !important;
	border-bottom-style: dotted !important;
	border-left-style: none !important;
	border-top-color: #20252B !important;
	border-right-color: #20252B !important;
	border-bottom-color: #20252B !important;
	border-left-color: #20252B !important;
}
/* other links */
#postlks a:link, #postlks a:visited, .cal a:link, .cal a:visited, .leftlinks a:link, .leftlinks a:visited, #logo a:link, #logo a:visited, #messages a:link, #messages a:visited, .bchead a:link, .bchead a:visited, #leftbar a:link, #leftbar a:visited, .w2 a:link, .w2 a:visited, #forums a:link, #forums a:visited {

	color: #20252B !important;
	text-decoration: none !important;
	border-bottom: none !important;
}
#postlks a:hover, #postlks a:active, .cal a:hover, .cal a:active, .leftlinks a:hover, .leftlinks a:active, #logo a:hover, #logo a:active, #messages a:hover, #messages a:active, .bchead a:hover, .bchead a:active, #leftbar a:hover, #leftbar a:active, .w2 a:hover, .w2 a:active, #forums a:hover, #forums a:active {
	color: #FF6600 !important;
	text-decoration: none !important;
	border-bottom: none !important;
}


/* banners */

#logo {
	background:#f2f2f2 !important;
	height: auto !important;
	font-size: 36px !important;
	font-weight: normal !important;
	width: 100% !important;
	margin: 0 !important;
	padding: 0px !important;
	text-transform: lowercase !important;
}

#topban {
	padding: 2px !important;
	background-color: #E6E9EC !important;
}

#topban h3 {
	display: inline !important;
	font-weight: normal !important;
	text-transform: uppercase !important;
	font-size: 36px !important;
}
.ban {
	font-size:100% !important;
	text-align:center !important;
	font-weight: normal !important;
	text-transform: uppercase !important;
	background-color: #E6E9EC !important;
	color: #566474 !important;
}
#messages font {
	color: #FF6600 !important;
}
.city b {
	background: #ddd !important;
	padding: 3px !important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 12px !important;
	font-weight: normal !important;
}


