css:
/* AGENT_SHEET */
 * {-moz-appearance: -moz-win-glass !important;
background-color:transparent!important;}
#main-window{opacity:.99!important}
body            {opacity:1!important}
body *          {opacity:.99!important}
body * > *      {opacity:1!important}
[src *= '.swf']{opacity:.99!important}
body  {background-color: black !important}
body > *{background-color: transparent!important}
/*â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“BOITES ou texte mis en valeurâ–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“*/
   button:hover,
   body input[name],
   body input[type]:hover,
   body select[name],
   body textarea[name],
  #ScratchPad scrollbar,
  #ScratchPadItem splitter,
   scrollbar scrollbarbutton:active,
   scrollbar thumb:active,
  .tabbrowser-tab[selected="true"],
   textbox,
  .textbox-textarea scrollbar,
   tree,
   treecol
{-moz-appearance: none !important;
 background:none transparent!important;
 -moz-border-radius: 10pt !important ;
  border: 1px inset !important;
 -moz-border-top-colors: #44473F !important;
 -moz-border-right-colors: #44473F !important;
 -moz-border-left-colors: #afafaf !important;
 -moz-border-bottom-colors: #CECECE !important;
 -moz-box-shadow: inset  -8px 8px 16px -8px black !important}
/*â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“BOITE 1â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“*/
   button,
   body input[type],
   scrollbar scrollbarbutton,
   scrollbar thumb,
  .tabbrowser-tab,
  .tabbrowser-tab:not([selected="true"])
{color:white!important;
 -moz-appearance: none !important;
 background:none transparent!important; 
 -moz-border-radius: 10pt !important;
  border: 1px inset !important;
 -moz-border-bottom-colors: #44473F !important;
 -moz-border-left-colors: #44473F !important;
 -moz-border-right-colors: #afafaf !important;
 -moz-border-top-colors: #CECECE !important;
 -moz-box-shadow: inset 8px -8px 16px -8px black,inset -8px 8px 16px -8px white, -8px 8px 16px -8px black !important}
 /*â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“BOITES ou texte mis en valeurâ–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“*/
  #main-window .tabbrowser-tab[selected="true"]:not([blah]) label,
   a:visited,
   a:hover,
   body input[name],
   body input[type]:hover,
   body select[name],
   body textarea[name],
   body input[id]:hover,
   button:hover,
  #CustomizeToolbarWindow,
  #identity-box,
   menu:hover,
   menupopup > menuitem[_moz-menuactive="true"],
   popup [_moz-menuactive="true"],
  #ScratchPad scrollbar,
  #ScratchPadItem splitter,
   scrollbar scrollbarbutton:active,
   scrollbar thumb:active,
  .tabbrowser-tab[selected="true"],
   textbox,
   textarea scrollbar,
   toolbarbutton.bookmark-item:hover,
   tree,
   treecol
{ color:black!important;
  text-shadow: black 1px -1px 2px, white -1px 1px 2px ,white -1px 1px 1px !important}
/*â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“BOITE 1â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“â–“*/
#main-window .tabbrowser-tab:not([selected="true"]):not([blah]) label,
   a,
   button,
   body,
   body input,
   menu,
   menupopup,
   popup,
   scrollbar scrollbarbutton,
   scrollbar thumb,
  .tabbrowser-tab:not([selected="true"]),
   toolbarbutton.bookmark-item,
   tooltip,window
{ color:white!important;
  text-shadow: black -1px 1px 1px, black -1px 1px 1px ,white 0px 0px 2px !important;}
/* ###########################################################################*/
   menu,toolbarbutton.bookmark-item,
   menupopup > menuitem,
   popup > menuitem
  {color:white!important;-moz-appearance: toolbarbutton !important}
   menu,menupopup,popup
  {opacity:1!important;border:none!important;-mox-box-shadow:none!important;padding:5px 5px 5px 5px!important}
/*#############################################################################*/
toolbox{border:none!important}
