css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nifflas.lpchip.nl") {


/*

   Saira's radio
    By Razzorman

*/


div[style*="http://nifflas.lpchip.nl/Themes/Jace/images/brlogo.gif"] {
margin-right: auto !important;
margin-left: auto !important;
height: 0 !important;
width: 0 !important;
padding-left: 800px !important;
padding-top: 146px !important;
background: url("http://i47.tinypic.com/2rdvrb6.jpg") !important;
border: 5px solid #191919 !important;
}


img[src="http://nifflas.lpchip.nl/Themes/Jace/images/posts_nn.gif"] {
width: 0;
height: 0;
padding-left: 46px;
padding-top: 46px;
background: url("http://i48.tinypic.com/54hg7q.png")!important;
}

img[src="http://nifflas.lpchip.nl/Themes/Jace/images/posts_nm.gif"] {
width: 0;
height: 0;
padding-left: 46px;
padding-top: 46px;
background: url("http://i46.tinypic.com/oshlyp.png")!important;
}

img[src="http://nifflas.lpchip.nl/Themes/Jace/images/posts_nc.gif"] {
width: 0;
height: 0;
padding-left: 46px;
padding-top: 46px;
background: url("http://i49.tinypic.com/2w6i98h.png")!important;
}



td[style*="rgb(204, 102, 170)"] {
border-right: 5px solid #FF3D3D !important;
}

td[style*="rgb(153, 170, 204)"] {
border-right: 5px solid #BBBBBB !important;
}

td[style*="rgb(68, 102, 204)"] {
border-right: 5px solid #60FF60 !important;
}



a:link, a:visited {
color:#FF2F2F !important;
text-decoration:none !important;
}

a:hover {
color:#FF2F2F !important;
text-decoration:underline !important;
}

a.nav:hover {
color:#FF2F2F !important;
}

.nav, .nav:link, .nav:visited {
color:#FF2F2F !important;
font-size:13px !important;
font-weight:normal !important;
}



.spoilerbody {
border:1px solid #FF2F2F !important;
}

.bbcode_edit_dif {
border: 1px solid #373737 !important;
}

.code {
max-height:800px !important;
}

.quote {
color:#CCCCCC !important;
}



#wrap {
background-color :#191919 !important;
border: none !important;
}

.windowbg {
background-color :#282828 !important;
}

.windowbg2 {
background-color :#2C2C2C !important;
border-bottom:none!important;
border-top:none!important;
}

.windowbg4, .windowbg3 {
background-color :#2F2F2F !important;
}

.windowbg5 {
background-color: #2F2F2F !important;
border:none!important;
}

.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
background-color:#0F0F0F !important;
}

.tborder {
border:1px solid #191919!important;
padding:0px!important;
}

.bordercolor {
background-color:#191919!important;
}

.catbg, .catbg2, .catbg3, tr.catbg td, tr.catbg3 td {
background-color:#373737!important;
background-image:none!important;
border-top:15px solid #191919 !important;
border-bottom:1px solid #191919!important;
}



html, body, td, th, tr {
color:#F6F6F6 !important;
}

b {
Font-weight:bold, lighter;
}



body {
background: none !important;
background-color: #191919 !important
}

input, textarea, select {
background: none repeat scroll 0 0 #424242 !important;
color: #F6F6F6 !important;
}

.maintab_active_back, .maintab_back {
background-color:#211304!important;
border-bottom:1px solid #000000!important;
border-right:1px solid #000000!important;
}

.maintab_first{
border-right:1px solid #000000!important;
}



}
