css:
/* Minimal Tabs/urlbar for the Moka theme from Rent0n. This is a mod from Minimalist Tab Bar by Saikobee */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tabs *|tab {    
  font-size: 10px !important;
  height: 12px !important;
  min-width: 8px !important;
}

#content spacer,.tabs-bottom {
  display: none !important;
}

#content *{
  margin: 0 !important;
  padding:0 0 0 0px !important;
}

tab>.tab-image-middle,tab,tabs { 
  max-height: 17px !important;
}

.tabbrowser-tab {
  -moz-appearance: none !important;
  -moz-border-radius: 0px !important;
  padding-left: 8px !important;
  padding-right: 8px !important;
}

.tabbrowser-tab[selected="true"] {
  background: #68696b !important;
  color: #e0e0e0 !important;
}

.tab-text {
  text-align: center !important;
}

#urlbar {
  background: #303030 !important; 
  text-align: right !important;
  color: #8f8f8f !important; 
}

#forward-button {
  background: #303030 !important;
  color: #8f8f8f !important;
}

#back-button {
  background: #303030 !important;
  color: #8f8f8f !important;
}

#reload-button {
  background: #303030 !important;
  color: #8f8f8f !important;
}

#urlbar-icons > #star-button:not([starred="true"]) {
  display: none !important;
}

#feed-button {
  display: none !important;
}

#urlbar-icons > #go-button {
  display: none !important;
}

.tabbrowser-tabs .tab-icon {
  display: none !important;
}

#back-forward-dropmarker {
  display: none !important;
}

.tabs-newtab-button {
display: none !important;
}

.scrollbutton-up, .scrollbutton-down {
  display: none !important;
}

/* scrollbar {
  visibility:collapse !important; 
} */

@-moz-document url("about:blank") {

* {
  background-color:#ECEDEE !important;
}
}
