css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {

/* Body */
body {background: #303030 !important;
color: #e0e0e0 !important;}

/* links */
a{text-decoration:none !important;
color: #e0e0e0 !important}
a.quotejs:hover, a:hover{font-weight:normal !important;text-decoration:underline !important;}

/* hr */
hr{display:none !important;}
form hr{display:block !important;}
hr{height:1px !important;border:none !important;background:#555 !important;}

/* rounded corners */
td.replyhl, td.reply{-moz-border-radius:2px !important;}

/* replay */
td.reply {background-color:#242424 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #555 !important;
color: #8f8f8f !important;
-moz-border-radius:3px !important;}

/* replay selected */
td.replyhl {background-color:#171717 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #555 !important;
color: #e0e0e0 !important;
-moz-border-radius:3px !important;}

/* name, more, ommitted, title, quote, filesizeinfo */
.postername, .commentpostername {color: #7EABE2 !important;}
.omittedposts, .abbr {color: #e0e0e0 !important;}
.replytitle, .filetitle {color: #7EE283 !important;}
.quotelink {color: #718f23 !important;}
.filesize {font-size: 7pt ! important; color: #8f8f8f;}

/* navigation */
#navtop, #navbot {display: none !important;} /* {font-size: 7pt ! important;
width: 90% !important;
text-align: left !important;
color: #4F4F4F !important;
padding: .4em !important;
top: 0;
z-index: 1;} */

/* pages */
.pages {background: #303030!important;
border: none !important;
font-size: 7pt ! important;}
.pages input { display: none !important; }
.pages td, .pages td b {color: #4F4F4F !important;}

/* deletefile */
.deletebuttons {font-size: 0pt !important;
text-align: right !important;}
.checkbox {display: none !important;}

/* box */
.boxbar {background: #444 !important;
color: #eee !important;}
.box-outer {border: none !important;
background: #333 !important;
color: #eee !important;}
.box-inner {background: #333 !important;
color: #eee !important;}

/* form */
form {margin: 1em !important;}

/* left postblock */
td.postblock {font-size: 7pt ! important;
background: #242424 !important;
border: 1px solid #555 !important;
text-align: right !important;
opacity: 1}

/* post block */
.inputtext, textarea {width: 271px !important;
opacity: 1}
pre, textarea {color: #606060 !important;
background: #ECEDEE !important;
border-left-color: gray !important;
border-right-color: gray !important;
border-top-color: gray !important;
border-bottom-color: gray !important;
opacity: 1}
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]),
select {
color: #606060 !important;
background: #ECEDEE !important;
border-left-color: gray !important;
border-right-color: gray !important;
border-top-color: gray !important;
border-bottom-color: gray !important;
opacity: 1;
width: 271px !important;}


/* remove blocks */
table,td{border:none !important;}
td.rules{display:none !important;}
img + br {display:none !important;}
.logo {display:none !important;}
.logo font b span {display:none !important;}
.rules{display: none !important;}
#hd {display: none !important;}
#ft{display: none !important;}
#filter-button, #option-button {display: none;}
th {display: none;}
.doubledash {visibility: hidden !important;}
#option-button, #navtopr, #footer, .logo, #navbotr, img.rotating, input[type="button"], td[align="right"] { display: none !important; }

/* no spam */
.rotating {display:none !important;}
a[href^="http://www.drowle.com/"]{display:none !important;}
a[href^="http://track.teenmodels.com/"]{display:none !important;}
[style^="width: 300px; position: absolute;left: 10%; border:1px solid black;"] {display:none !important;}

}
