css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

tbody.vC {
  padding: 0px !important;
  margin: 0px !important;
  border: none 0px !important;
}

tbody.vC tr.vI {
  padding: 0px !important;
  margin: 0px !important;
  border: none 0px !important;

  width: 200px !important;
  display: block !important;
  font-size: 0px !important;
  overflow: hidden !important;
}

tbody.vC tr.vI td {
  padding: 0px !important;
  margin: 0px !important;
  border: none 0px !important;

  display: inline-block !important;
  font-size: 0px !important;
}

tbody.vC tr.vI td.vr {
  overflow: visible !important;
  height: 0px !important;
}

tbody.vC tr.vI td img {
  margin: 2px 0px 1px 2px !important;
}

tbody.vC tr.vI td img.df, tbody.vC tr.vI td img.de {
  height: 0px !important;
  width: 200px !important;
}

tbody.vC tr.vI td.vr span {
  padding: 0px !important;
  margin: 0px !important;
  border: none 0px !important;

  font-size: 9.5pt !important;
  line-height: 2pt !important;  
}

tbody.vC.vD tr.vI {
  background-color: #ffffcc !important;
}

tbody.vC.vE tr.vI {
  background-color: #ff8a00 !important;
}

tbody.vC.vE tr.vI td.vr span {
  line-height: 0pt !important;  
}

tbody.vC.vE {
  border-bottom: solid 1px #ffffff !important;
}

tbody.vC tr.vm td {
  min-width: 18px !important;
}

}
