css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.mangafox.com") {  

/* Ad Removal */
	a[href="http://www.game321.com/?utm_source=mangafox&utm_medium=skin_banner&utm_term=free&utm_campaign=fs_mf_skin"], a[href="http://fs.game321.com/?utm_source=mangafox&utm_medium=skin_banner&utm_term=free&utm_campaign=fs_mf_skin"], a[href="http://fs.game321.com/html/activity/carnival/index.html?utm_source=mangafox&utm_medium=skin_banner&utm_term=free&utm_campaign=fs_mf_skin_201007"], a[href="http://fs.game321.com/html/activity/carnival/index.html?utm_source=mangafox&utm_medium=skin_banner&utm_term=free&utm_campaign=fs_mf_skin_20100721"], a[href="http://www.mangafox.com/games/"], a[href="http://caesary.game321.com/?utm_source=mangafox_caesary&utm_medium=cpa&utm_campaign=caesary"] {
		display:none !important;
	}

/* Remove Seasonal Background Images */
	#body a:first-child + div, #body a:first-child + div + div, #body a:first-child + div + div + div, body a:first-child + div + div + div + div {
		background:none !important;
	}
	a[href="/games/"] div {
		display:none !important;
	}


/* Remove Front Page Flash Ads */
	div#page[class="widepage"] embed {
		display:none !important;
	}

/* Dark Theme */
	body {
		background: #000 !important;
		color: #666 !important;
	}

	.gap, #chat, div#intro, #bookmark, #bottom_left_bar, #bottom_right_bar, #top_left_bar, #top_right_bar, #bottom_ads, #topad, #showcase, .ad728x90, #footer, div>div[class="right"], #advertisment, div#rss, iframe, div#idSlideshow {
		display: none !important;
	}

	ul#menu {
		float:left !important;
		margin-left: 68px !important;
	}

	div[class="cr warning"] {
		background: #330000 !important;
	}

	div[class="cr warning"] a, div[class="cr warning"] a:visited {
		color: #FFF !important;
	}

	#information {
		margin-top: 10px !important;
	}

	#page {
		padding: 0 !important;
		background: #000 !important;
	}

	#viewer {
		margin-top: 10px !important;
		background: #333 !important;
		-moz-border-radius: 5px !important;
		border-width: 1px 5px !important;
	}
	#viewer>a>img {
		border: 5px solid #666 !important;
		-moz-border-radius: 5px !important;
		padding: 0 !important;
	}
	#tool {
		display: none !important;
		background: #000 !important;
		border-bottom: solid #333 5px !important;
	}

	#series {
		width: 100% !important;
		height: 100% !important;
		margin: auto !important;
		text-align: center !important;
		vertical-align: middle !important;
	}

	div[style="padding: 10px;"] {
		padding: 0px 10px 10px 10px !important;
	}

	#statistics, .contributor, #latest, .manga, .row_alt, table[id="listing"]>tbody>tr>th, table[id="updates"]>tbody>tr>th {
		background: #111 !important;
	}

	#listing tr.volume td {
		background: #111 !important;
	}

	a {
		color: #666 !important;
	}

	#updates, #listing, #information th, #weeklymanga th {
		color: #666 !important;
	}

	* {
		border-color: #333 !important;
	}

	#top_center_bar {
		background: #000 !important;
		height: 50px !important;
		overflow: hidden !important;
		margin-left: 8px !important;
		display: none !important;
	}

	#bottom_center_bar {
		background: #000 !important;
		margin-left: 7px !important;
	}

	input, select {
		background: #111 !important;
		color: #666 !important;
		-moz-appearance: none !important;
		-moz-border-radius: 4px !important;
	}


	a[class="button prev_page"], a[class="button next_page"] {
		display: none !important;
	}
}

@-moz-document url-prefix("http://www.mangafox.com/manga/") {

	.gap, #chat, #bookmark, #bottom_left_bar, #bottom_right_bar, #top_left_bar, #top_right_bar, #bottom_ads, #topad, #header, #showcase, .ad728x90, #footer, div>div[class="right"], #advertisment {
		display: none !important;
	}

/* Remove Seasonal Background Images */
	#body div:first-child + a + div, #body div:first-child + a + div + div, #body div:first-child + a + div + div + div, #body div:first-child + a + div + div + div + div {
		background:none !important;
	}

/* Remove Tips at bottom of reading pages */
	.reader_tip, div#tip {
		display:none !important;
	}

/* Remove Text Navigation at bottom of reading pages - Drop Downs are good enough */
	div#chnav, div#pgnav, div#related {
		display:none !important;
	}

}
