css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://afternet.org/idle/") {

a { color: lightgreen !important; }
a:hover { color: lime !important; }

body, div#popbox { background-color: #333333 !important; color: white !important; }

div.head { display: none !important; }
div.content, div#menu, div.notice, div.footer { background-color: black !important; }

table.ttls td, table.ttls th, div#menu a,
table.ddbb td, table.ddbb th { border: 1px solid #888 !important; padding: 1px !important; }


}
