css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.rememberthemilk.com") {
   #appheaderlogo, .appfooter, #onlinehelpwrap, .snake-line, .snake-left-link, .snake-right-link { display:none; }

#content {position: absolute !important; left: 115px !important; width: 88.8% !important;height: 100% !important;}

 #listsearchfor {font-style: italic !important; color: silver !important; font-size: 0.8em !important; padding: 10px 0 !important;}
.orange_rbroundbox {position: fixed !important; top: 45% !important; width: 50% !important; left: 30% !important; z-index: 2 !important; padding: 6px !important; border: solid 1px black !important; -moz-border-radius: 10px !important;opacity: 0.7 !important;}

.orange_rbbot, .orange_rbtop {display: none !important;}

/* header information */

#detailscontent {margin: 0 !important; padding: 0 !important;}

#detailstitle {margin-left: 15px !important; font-size: 1.2em !important; margin-top: -10px !important; padding-bottom: 5px !important;}

#appheader {position: fixed !important; right: 2% !important; top: 36px !important;width: 77.8% !important; z-index: 30 !important;font-size:0.8em !important;}

#detailsbox {position: fixed !important; padding-top:31px !important; top: 0px !important;width: 88.6% !important; opacity: 0.9 !important;left:115px !important; background: white !important;}


#listdetails, #details, #detailswrap {width: 100% !important;}

#details, #detailswrap, #detailsbox {height: 114px !important;}

#taskdetails > * {min-height: 21px !important; float: left !important; margin-left: 10px !important; min-width: 22% !important;}

#listdetailsbox > * {margin-left: 10px !important; min-height: 21px !important; float: left !important;  min-width: 20% !important;font-weight: bold !important; }

#taskdetails > * > SPAN {font-weight: bold !important;}

#publishform > INPUT[type="submit"] {position: absolute !important; top: 80px !important; right: 20px !important; }
#publishform, #listsharedetails {margin-left: 10px !important;}
/* selected details */

#detailsstatuswrap {position: fixed !important; top: 90% !important; width: 30% !important; left: 35% !important; } 

/* task list */

#list {position: absolute !important; top: 147px !important; width: 100% !important; } 
.xtable > TBODY > .xtr:nth-child(odd) {
background: #fafafa !important;
}

.xtable > TBODY > .xtr:nth-child(even)  {
background: #FFF !important;
}


/* tabs */

#listtabs { padding-top: 56px !important; position: fixed !important; left: 0% !important; top: 0px !important;width: 114px !important;height: 100% !important; background: white !important; opacity: 1.0 !important;}

/* skin smart lists differently */ 
#listtabs > UL >  * { background: none !important;   text-align: right !important; height: 25px !important; border: none !important;font: 9pt/15pt Calibri !important;margin:0 !important;}

#listtabs > UL > LI > A { background: #EDEFEF !important; margin-left: -16px !important; width: 96px !important;-moz-border-radius : 6px 0 0 6px !important;border: 1px black solid !important; color: #1D1D1D !important;}

#listtabs > UL > .xtab_smartlist > A {background: #DEDEEF !important;}

#listtabs > UL >.xtab_selected > A {border: 1px maroon solid !important; border-right: none !important; background: white !important;width: 97px !important;  }



#notes {position: fixed !important; top: 12% !important; background: white !important; left: 18% !important; width: 70% !important; border: solid 1px black !important; -moz-border-radius: 5px !important; opacity: 0.9 !important; z-index: 30 !important;}
.notexedit, .notewidth {margin-left: 3px !important; width: 99% !important;}



/* task cloud */

#taskcloud_copy > * > * ,#taskcloud_copy > * > * > *  {overflow: hidden !important; height: 0 !important; margin: 0 !important;}

#taskcloudcontent_copy {position: fixed !important; top: 90% !important; width: 90% !important;opacity : 0.8 !important; left: 5% !important; border: 1px black solid !important; -moz-border-radius: 5px !important; background: white !important;}


   .tasktag,.locationtag  { }

/*
.level1 {font-size: 0.9em !important;}
.level2 {font-size: 1.1em !important;}
.level3 {font-size: 1.3em !important;}
.level4 {font-size: 1.5em !important;}
.level5 {font-size: 1.7em !important;}
*/

#notetoolbox, #listtoolbox {margin-top: 12px !important; text-align: right !important;margin-right: -6px !important;} 

#searchbox {position:absolute !important; margin: 0 !important; padding : 0 !important; right: 0 !important; width: 100% !important; top: -30px !important; z-index: 3 !important; background: white !important;}

#searchboxwrap > TABLE { width: 100% !important; }
   #searchtogglewrap { margin-top: -16px !important;width:inherit !important; }
   #searchboxwrap { background:none; width: 79% !important; }

 #searchoptions {margin: 0 !important; padding : 0 !important;position: fixed !important; top: 70px !important;border: 2px black solid !important; -moz-border-radius: 5px !important;z-index: 50 !important;background: white !important; opacity: 0.9 !important;width: 79% !important; left: 115px !important;}

   #statusbox { margin:20px 0 0 0 !important; padding:0 0 0 6px !important; }

#sorting {position: fixed !important; top: 13px !important; left: 10px !important; z-index: 300 !important; font-size: 0.6em !important;}

/* keyboard shortcuts */

  #detailstitle_highlight[title]::after {
    content: " (r)";
    color: #575757;
    font-weight: normal;
  }
  #detailsdue::after       { content: " (d)"; }
  #detailsreoccur::after   { content: " (f)"; }
  #detailsduration::after  { content: " (g)"; }
  #detailstags::after      { content: " (s)"; }
  #detailslocation::after  { content: " (l)"; }
  #detailsurl::after       { content: " (u)"; }
  #detailspostponed::after { content: " (p)"; }
  #detailsnotes::after     { content: " (y)"; }

  #tasksToolbox > .xtoolbox_selector > a:first-child::after { content: " (a)"; }
  #tasksToolbox > .xtoolbox_selector > a:last-child::after  { content: " (n)"; }
  #addEntry > a::after { content: " (t)"; }

  #tasktabs:after, #detailstabs::after {
    content: "(h)";
    line-height: 2;
    margin: 0 0.5em;
  }

  #searchtogglewrap::before {
    content: "(C-S-/) ";
    margin-left: -10em;
  }

  #viewSelector > a[href="#section.overview"]::after  { content: " (C-S-6)"; }
  #viewSelector > a[href="#section.tasks"]::after     { content: " (C-S-7)"; }
  #viewSelector > a[href="#section.locations"]::after { content: " (C-S-8)"; }
  #viewSelector > a[href="#section.contacts"]::after  { content: " (C-S-9)"; }
  #viewSelector > a[href="#section.settings"]::after  { content: " (C-S-0)"; }
  #viewSelector > a::after { font-size: 80%; }
  #appheaderlogo { position: absolute !important; }
  #topnav { position: relative !important; }

  #statusboxUndo > a::after { content: " (z)"; }

  #tasksToolbox > .xtoolbox_actions > form > div::after {
    content: " Complete (c), Postpone (p), Up (k), Down (j), Select (i), Undo (z), Multi (m)";
    color: #757575;
  }
  #tasksToolbox::after {
    content: "SMART ADD: Due (^), Priority (!), List/Tag (#), Location (@), Repeat (*), Time (=), Off (~)";
    color: #757575;
    display: block;
    white-space: pre;
    margin: 5px 0 -0.75em 20px;
  }
}
