@namespace url(http://www.w3.org/1999/xhtml);
/* Makes it appear regardless AND marks visited offers */
@-moz-document domain("facebook.com") {
a[href^="http://apps.facebook.com/"]:visited {text-decoration: line-through !important;}
}

/* Mafia Wars */
@-moz-document 
url-prefix("http://www.facebook.com/home.php?filter=app_10979261223"),
url-prefix("http://www.facebook.com/home.php?filter=app_10979261223"),
url-prefix("http://www.facebook.com/home.php?filter=app_10979261223") {
#home_stream > LI:not([class]),
#home_stream > [class]:not([class*="10979261223"])
{display: none !important;}
DIV.clearfix.uiHeaderTop:only-child H2.uiHeaderTitle:before {content: "SPP Ranch! " !important;}
}

