css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#appmenu-button-container{
position: fixed !important;
}

#navigator-toolbox[tabsontop="true"] #TabsToolbar{
padding-left: 125px !important;
}

window:not([chromehidden=""]) 
#appmenu-button {
 display: none !important;
}

#navigator-toolbox[tabsontop="true"] #TabsToolbar
{
padding-left: 93px !important;
}
#appmenu-button{
padding: 3px 18px 3px 12px !important;
}
#appmenu-button .button-text { display:none !important; }
#appmenu-button dropmarker:before { content: "Firefox " !important; }

#appmenu-button
{
-moz-border-radius-bottomright: 10 !important;
-moz-border-radius-bottomleft: 10 !important;
height: 20px !important;
}
#appmenu-button-container
{
position: fixed !important;
top: 5px !important;
}

/* General tab settings */
tab {
 margin-top: 13px !important; /* Space over tabs and under URL-tab */
 margin-bottom: 0px !important;
 border-bottom: 1px !important;
}
