css:
@-moz-document url-prefix(http://google.), url-prefix(http://www.google.), url-prefix(https://google.), url-prefix(https://www.google.) {

/* remove ads and stuff */
#mbEnd, #rhsline.std, #pa1, #tads, #tads.c, .tas, #ImgCont > TABLE[style*='background: rgb(255, 248, 221)'], #mclip, .ads.noprint, #rv_panel, .serp TD.right-nav, .serp TD#table-spacer-cell, #ads-rhs, .pY, .u5, .u8, #lhid_search_ad_unit, #rhsa, #rhswrapper > TABLE#rhssection TR, BODY[onunload='google.Quote.stopQuotes();'] DIV[class='sfe-break-bottom-16'], DIV.sfe-break-top.sfe-break-bottom-16, BODY[marginheight="2"][bgcolor="#ffffff"][topmargin="2"] > TABLE[cellspacing="0"][cellpadding="0"][border="0"][width="25%"][bgcolor="#ffffff"][align="right"], .ads.topads, #rhsads, [src*='ads.pheedo.com'], [src*='www.techcrunch.com/wp-content/themes/techcrunchmu/ads/'], [src*='ads.gigaom.com'], .item-body div p[align='right'], [href*='d.ads.readwriteweb.com'], #ads-top, #ads-bot, [href*='doubleclick.net'], [href*='feedads.'], #np_content_ads_module, #main-wrapper #main-table table.left > tbody > tr > td[colspan='2'], .top-ads, .sponsored-links, .top-ad, .g-c #ad, [HREF*='http://ads.pheedo.com'] {display:none !important;}


/* increase results width */
/* changed to 990px to make previews work properly */
div#cnt { min-width:99% !important; }
#center_col {margin-right:0px!important; max-width:990px% !important;}
#cnt #res .g .s {max-width:99% !important;}
#cnt #center_col, #cnt #foot { width:99% !important;}
.mw { max-width:99%; }
.big .mw { max-width:99%; }


/* remove sidebar */
div#leftnav {display: none !important; }
#center_col {margin-left: 0px !important; }


/* disable fade (obsolete) */
/* #ghead, #sbl, .fade, #fctr{ opacity: 1 !important; } */


/* change font */
* {
    font-family: "Segoe UI", Tahoma !important;
}

/* remove right side column */
#rhscol { display:none !important; }

/* Preview stuff */
#nycp {
background-color: #FFFFFF;
border-left: 1px solid #EBEBEB;
bottom: 0;
left: 0;
margin-left: 285px;
min-width: 240px;
max-width: 410px;
padding: 0px 0px 0px 0px;
position: absolute;
right: 0;
top: 0;
visibility: hidden;
}

}
