css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {



#contentCol  { background-color:#FEFFF3 !important; }
.profile .profile_color_bar  { background-color:#FEFFF3 !important; }


/* navigation */
#blueBar       { background:#DDF078 !important; }
#pageLogo a    { background:url(http://i.imgur.com/nLoUY.png) no-repeat scroll -21px 0 !important; }
#headNavOut    { background:#F2F6B2 !important; border-color:#CADE26 !important; }
	


/* main nav icons */
.jewelToggler  { background-image:url(http://i.imgur.com/nLoUY.png) !important; }
#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,
#jewelMail:hover, #jewelMail:focus, #jewelMail:active,
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#F2F6B2 !important; }
#jewelCase .jewel  { border:none !important; }



/* search */
#navSearch button  { background:url(http://i.imgur.com/nLoUY.png) no-repeat scroll -1px 0 !important; }
#navSearch     { border-color:#DDF078 !important; }



/* nav tabs */
#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#CADE26 !important; color:#ffffff !important; }



/* boxes */
.lightblue_box  { background-color:#FAFCE2 !important; border-color:#F2F6B2 !important; }
.commentable_item .ufi_section  { background-color:#FAFCE2 !important; }
textarea, .inputtext, .inputpassword  { border-color:#F2F6B2 !important; }



/* links */
a  { color:#CADE26 !important; }
.uiLinkButton input  { color:#2c2c2c !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }
button.as_link  { color:#2c2c2c !important; }



/* side nav */
.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#F2F6B2 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#FAFCE2 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#FAFCE2 !important; }

.unread  { background-color:#F2F6B2 !important; }
.line  { color:#CADE26 !important; }



/* pop up box  */
#jewelCase .jewelFooter a   { background-color:#FAFCE2 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#CADE26 !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#CADE26 !important; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#FAFCE2 !important; border-color:#DDF078 !important; }
#jewelCase .jewelBox  { background-color:#FEFFF3 !important; }



/* copy */
.uiHeader h2  { color:#CADE26 !important; }



/* graffiti */
.current_stuff td  { background-color:#FEFFF3 !important; border-color:#F2F6B2 !important; }



/* profile page */
.UIComposer .UIComposer_Box  { background-color:#FEFFF3 !important; border-color:#F2F6B2 !important; }
.profile .box .box_header  { background-color:#FEFFF3 !important; border-color:#F2F6B2 !important; }



/* profile tabs */
.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#F2F6B2 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#F2F6B0 !important; border-color:#DDF078 !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#CADE26 !important; color:#ffffff !important; border-color:#CADE26 !important; }




/* chat */
#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#FEFFF3 !important;  }
#chat_tab_bar .chat_header  { background-color:#DDF078 !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#F2F6B2 !important; color:#CADE26 !important; }
#presence_bar .presence_menu_opts h2 span  { background:#DDF078 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#DDF078 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#FEFFF3 !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#DDF078 !important; border-color:#DDF078 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }

#presence .presence_menu_opts  { border-color:#DDF078 !important; }



/* buttons */
.UIButton_Blue  { background:#CADE26 !important; border-color:#DDF078 !important; }
.UIComposer_InputArea  { border-color:#F2F6B2 !important; }
.uiButtonConfirm  { background:#CADE26 !important; border-color:#DDF078 !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }







}
