css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {
/* generic */
body {
	margin-top: 3.4em !important;
	border-right: 21.4em solid #E6EDF7 !important;
}
*::-moz-selection {
	background: #000 !important;
	color: #fff !important;
}

a:link, a:visited {
	text-decoration: none !important;
	color: #0765E5 !important;
}
a:hover {
	color: #069 !important;
}
#header a:link, #header a:visited {
	color: #acd !important;
	border-bottom: none !important;
}
#header a:hover,
#header h1 a[href="/"] {
	color: #2983EA !important;
}
#infobar a:link, #infobar a:visited {
	color: #aef !important;
}
#infobar a:hover {
	color: #fff !important;
}
a:focus, #header a:focus, #infobar a:focus,
a:active, #header a:active, #infobar a:active {
	outline: 0 !important;
	color: #000 !important;
	background: #ff9 !important;
}
input, select {
	border-width: 1px !important;
}
/* header */
#header {
	position: fixed !important;
	top: 0 !important;
	z-index: 11 !important;
	padding: 0 1em 0 0.9em !important;
	background: #000 !important;
	color: #ccc !important;
}
#header h1,
#header .auth-h1 {
	display: inline !important;
	padding: 0 !important;
	font-size: 0.9em !important;
}
#header h1:before {
	content: url("data:image/gif;base64,");
	padding-right: 0.5em;
}

#header #header-user-links {
	display: inline !important;
}
#header-l {
	min-height: 0 !important;
	background: none !important;
	padding: 0 !important;
}
#header-global-links {display: none !important;}
#header-auth-links {
	padding-top: 0.5em !important;
}
#infobar, #banner-hr {
	position: fixed !important;
	top: 2em !important;
	z-index: 10 !important;
	width: 100% !important;
	margin: 0 !important;
	padding: 0 0 5px !important;
	border: 0 !important;
	background: #fff url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAFCAIAAAAL5hHIAAAAA3NCSVQICAjb4U/gAAAACXBIWXMAAAsSAAALEgHS3X78AAAAFnRFWHRDcmVhdGlvbiBUaW1lADI0LzEyLzA1T7TRugAAACV0RVh0U29mdHdhcmUATWFjcm9tZWRpYSBGaXJld29ya3MgTVggMjAwNId2rM8AAAAcSURBVHicY5g+fTqThIQEk4KCApOEhATD9+/fAS9gBaEFEjq6AAAAAElFTkSuQmCC") repeat-x bottom !important;
}
#header + #banner-hr {
	top: 1.6em !important;
	height: 1.5em !important;
	background: #2983EA url("data:image/png;base64,") repeat-x bottom !important;
}
#header + #banner-hr:after {
	content: ".";
	display: block !important;
	margin: 1em 0 0 0;
	text-indent: -3000em;
	background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAFCAIAAAAL5hHIAAAAA3NCSVQICAjb4U/gAAAACXBIWXMAAAsSAAALEgHS3X78AAAAFnRFWHRDcmVhdGlvbiBUaW1lADI0LzEyLzA1T7TRugAAACV0RVh0U29mdHdhcmUATWFjcm9tZWRpYSBGaXJld29ya3MgTVggMjAwNId2rM8AAAAcSURBVHicY5g+fTqThIQEk4KCApOEhATD9+/fAS9gBaEFEjq6AAAAAElFTkSuQmCC") repeat-x bottom !important;
}
#infobar .search-box {
	position: absolute !important;
	right: 0 !important;
	padding: 0 0 5px !important;
	width: 21.4em !important;
	font-size: 1.25em !important;
	background: url("data:image/png;base64,") repeat-x bottom !important;
}
#infobar .search-box form {
	padding: 0.3em 0 0.35em 0 !important;
}
#infobar #page-desc {
	padding: 0.5em 1em !important;
	background: #2983EA url("data:image/png;base64,") repeat-x bottom !important;
}
#infobar #page-desc.multi-line br {
	display: none;
}
#infobar #page-desc.multi-line br + *:before {
	content: " | ";
	color: #000;
}
/* content */
#settings_nav {
	margin: 0 !important;
	padding: 1em 0 0 !important;
}
#main {
	margin: 0 !important;
	padding: 0 !important;
}
#main .pager,
#main h3 {
	margin: 0 !important;
	padding: 1em !important;
}
#main .abouthead {
	padding: 1em 1em 0.5em !important;
	color: #000 !important;
}
#main .smaller,
#main p,
#main form,
#main h2,
#main h3,
#main h4,
#main h5 {
	padding: 1em !important;
}
#main .smaller p,
#main li h4 {
	padding: 0 !important;
}
#main #bundle-edit {
	padding: 0 1em;
}
.post .meta a.pop {
	color: #500 !important;
}
.posts {
	border-top: 1px solid #eee !important;
}
.posts .post {
	border-bottom: 1px solid #eee !important;
	padding: 1em !important;
}
.posts .post:hover {
	background: #ff9 !important;
}
.posts .post h4 {
	line-height: 1.5em !important;
}
.postui ul li.bundle {
	padding: 0 1em 0 2em !important;
}
/* sidebar */
#sidebar {
	float: right !important;
	margin-right: -21.4em !important;
	width: 21.5em !important;
	border: 0 !important;
}
.sidebar-inner {
	border: 0 !important;
	background: none !important;
}
#sidebar .bundle {
	width: auto !important;
}
/* footer */
#footer {
	padding: 0 !important;
}
#footer-inner {
	margin: 0 !important;
}
#items-per-page {
	padding: 0 1em !important;
}
#footer-hr {display: none !important;}
#footer-inner ul {
	margin: 1em 0 0 0 !important;
	padding: 1em !important;
	border-top: 1px solid #eee !important;
}
/* front page */
body.fp {
	border: 0 !important;
}
body.fp #main {
	padding: 1em !important;
}
.numbox a:link,
.numbox a:visited {
	color: #fff !important;
}

#help-page h1,
#help-page h2,
#help-page h3,
#help-page h4,
#help-page h5,
#help-page h6,
#help-page ul {
	padding: 1em !important;
	color: #000 !important;
}
#leftside {
	margin: 0 !important;
	padding: 1em !important;
}
#offset-page {
	margin: 0 !important;
}
}
