css:
/* Original by BoShow7, tweaked for new layout */
/* Inspired by Dakata and Agnelo */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tvtropes.org") {

/* Modify logo to make it visible on black background */
img[src*="http://tvtropes.org/pmwiki/pub/images/lampshade_logo_blue.png"], img[src*="http://static.tvtropes.org/pmwiki/pub/images/lampshade_logo_blue.png"] {
    visibility:hidden !important;
    }
 div#wikilogo form center table tr td>a, div td center table tr td>a {
    background: url(data:image/png;base64,) 8px 0px no-repeat !important;
    width: 133px !important;
    height: 40px !important;
    float: right !important;
    }

/* Kill these */
 iframe, ins, img[src="http://creativecommons.org/licenses/by-sa/3.0/"] {
    display: none !important
    }

/* textboxes */
 textarea, input[type=text], input[type=password] {
    -moz-appearance: none !important;
    }

 textarea {
    color: #bbb !important;
    background-color: #080808 !important;
    border: 1px solid #555 !important;
    padding: 5px !important;
    width: 95% !important;
    }

 input[type=text], input[type=password] {
    color: #bbb !important;
    background-color: #111 !important;
    border: 3px solid #555 !important;
    }

/* tools border */
 #wikileftpage h1 {
    border-width: 2px !important;
    }

/* Watchlist stuff is back */
 div#watchmsg {
    background-color: transparent !important;
    position:relative !important;
    bottom:2px !important;
    }

/* Title colors go yellow */
 div.pagetitle span {
    color: yellow !important;
    }

 /* Alternate title text color goes orange */
 div.alt_title {
    color: #FFAE00 !important;
    }

/* Bunch of things go completely black, and the main text color goes white */
 body, h1 {
    background-color: #000 !important;
    color: #bbb !important;
    }
 #wikitext h1 {
    border: 1px solid #555 !important;
    }

/* Main dark grey background color and getting some of the other text to go white */
 div.container, td, ul, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }

/* Most buttons will behave like this */
 tr td div.buttoninterior {
    color: yellow !important;
    }
 tr td div.buttonframe div.buttoninterior a {
    color: #fdc423 !important;
    display: block !important;
    }
 div.buttonframe, div.buttoninterior, div.folderlabel, div.templatebuttoninterior, div.templatebuttonframe {
    background-color: #222 !important;
    }
 tr td div.buttoninterior:hover, tr td div.buttonframe:hover {
    border-color: yellow !important;
    }

/* Open/Close All Folders button gets purple text with dark background */
 div.folderlabelopen {
    background-color: #222 !important;
    color: #E600FF !important;
    border-color: #444 !important;
    }
 div.folderlabelopen:hover {
    border-color: #777 !important;
    }

/* Folders under that */
 div.folderlabel, div.folderlabel span {
    color: #d5db20 !important;
    border-color: #444 !important;
    }
 div.folderlabel:hover {
    border-color: #777 !important;
    }

/* Changing the colors of the Google search bar stuff and makes the grey background rounder */
 div.cse-branding-bottom {
    background-color: #222 !important;
    color: #fff !important;
    -moz-border-radius: 10px !important;
    padding-right: 9px !important;
    margin-left: 9px !important;
    border: 1px solid #555 !important;
    }

/* new navigation */
 table.pathholder {
    background-color: #222 !important;
    border-color: #555 !important;
    height: 100% !important;
    }
 table.pathholder td {
    background-color: #222 !important;
    }
 table.pathholder td a img {
    visibility: hidden !important;
    }
 table.pathholder td a.navleft {
    display: block !important;
    background: url(data:image/png;base64,) left no-repeat !important;
    }
 table.pathholder td a.navright {
    display: block !important;
    background: url(data:image/png;base64,) right no-repeat !important;
    }

/* Other navigation bars */
 div.pathholder {
    background-color: #222 !important;
    border: 1px solid #555 !important;
    }

/* Changes the captions under pictures to green and makes the caption box round */
 div.acaption, div.acaptionright {
    background-color: #252525 !important;
    border: 1px solid #555 !important;
    color: #00cc33 !important;
    position:relative !important;
    z-index: 3 !important;
    }
 div.quoteright {
    background-color: transparent !important;
    border-color: #555 !important;
    }
 div.acaption span, div.acaptionright span, div.acaption a:link, div.acaptionright a:link, div.quoteright a:link {
    color: #00cc33 !important;
    }
 div.acaption a:visited, div.acaptionright a:visited, div.quoteright a:visited {
    color: #008020 !important;
    }
 div.acaption a:hover, div.acaptionright a:hover, div.quoteright a:hover {
    color: #d3b050 !important;
    }

/* Quotes go dark grey with blue text */
 div.indent {
    color: #00BFFF !important;
    }
 div.indent a:link {
    color: #00BFFF !important;
    }
 div.indent a:visited {
    color: #00759C !important;
    }
 div.indent a:hover {
    color: #d3b050 !important;
    }

/* Noticeheaders (like "Just for fun" or "Subjective" */
 div.noticeheader {
    background-color: #333 !important;
    color: #FF00F2 !important; 
    font-size: 16px !important;
    border: 1px solid #555 !important;
    }

/* "Examples" header goes greyer and "This page is under discussion" header and some other things goes greyer (lighter than the background though) */
 h2, h3, div.contributorlead, div.justbugscaption, div.wmglead {
    background-color: #222 !important;
    color: #bbb !important;
    border: 1px solid #555 !important;
    }

/* Attributes for links */
 a:link {
    color: #ccc !important;
    text-decoration: underline !important;
    }
 a:visited {
    color: #fdc425 !important;
    text-decoration: underline !important;
    }
 a:hover {
    color: #d3b050 !important;
    }

/* Attributes for links that go nowhere */
 a.createlink:link, a.createlink:visited {
    color: #ac7069 !important
    }
 a.createlink:hover {
    color: #d26f42 !important;
    }

/* Removes some table borders */
 li, a {
    border: none !important;
    z-index: 1 !important;
    position: relative !important;
    }

/* Sidebar and floatboxstuff */
 li.plus, div.floatboxright, div.floatboxright ul {
    background-color: transparent !important;
    z-index: 2 !important;
    position: relative !important;
    margin-top: 0px !important;
    }

/* Changes the bullet boxes in the sidebar so you can see them */
 li.plus {
    list-style-image:url(data:image/gif;base64,R0lGODlhCwALAIABALu7u////yH5BAEAAAEALAAAAAALAAsAAAIXhI8Wy6ze3gKRQkeTksFO31WR2FQaUgAAOw==) !important;
    }

/* Changes the Google image under the search bar */
 img[src*="http://www.google.com/images/poweredby_transparent/poweredby_FFFFFF.gif"]{
    width: 0px !important;
    height: 27px !important;
    padding-left:26px !important;
    background:url(data:image/jpeg;base64,) no-repeat !important;
    }

/* Changing the color of text before you edit a page to red... (some other things go red) */
 p {
    color: #ff0000 !important;
    }

/* Darker edit titles plus other things */
 div.difftime, div.score, div.voteactivity, div.scoreboard {
    background-color: #333 !important;
    z-index: 2 !important;
    position: relative !important;
    }

/* Reviews */
 div.review_text {
    background-color: #222 !important;
    color: #bbb !important;
    }

/* Forum title colors */
 div.pagetitle, th { 
    color: #bbb !important;
    z-index: 2 !important;
    position: relative !important;
    }

/* Forum buttons are changed */
 a.forumbutton {
    background-color: #333 !important;
    }

/* The background color of forum members' names goes black */
 a.tropername {
    background-color: #000 !important;
    }

/* smiley bar */
 div#smileyPanel {
    background-color: #222 !important;
    }

/* some buttons/links get higher z-index */
 a.fauxbutton, div.fauxbuttonholder {
    z-index: 3 !important;
    position: relative !important;
    background-color: #333 !important;
    }

/* changes colors of minor change notices in recent changes */
 li.newminor {
    background-color: #222 !important;
    }

/* Recents edits for box */
 div#Toolcats div, div#Toolcats, div#indexen {
    background-color: #222 !important;
    }

 div#Goodiescats li, div#Toolcats li, div#Toyscats li {
    background-color: transparent !important;
    }

/* Restoring some link colors */
 a.comixlink:link {
    color:#666600 !important;
    }
 a.DarthWikilink {
    color:white !important;
    }
 a.SoYouWantTo, a.newgrouplink {
    color:#339900 !important;
    }
 a.discussionlink, a.DarthWikiDiscussionlink {
    color:#D2691E !important;
    }
 a.yklink, a.fauxww, a.rename {
    color:purple !important;
    }
 a.AATAFOVSlink {
    color:#543C1C !important;
    }
 a.justbugsme, a.wmg {
    color:#009999 !important;
    }

/* What's happening */
div#Happening li {
    background-color: #222 !important;
    }

/* Spoiler tags */
 span.spoiler,  .spoiler a.createlink,  .spoiler a, span.spoiler a.twikilink, span.spoiler span, span.supersecret {
    color: transparent !important;
    }

/* Color of spoilers (disregard this if you highlight spoilers) */
 span.spoilerRevealed {
    color: yellow !important;
    }

/* For those spoilers that you have to click on */
 span.tiptag sup {
    position: relative !important;
    top: 6px !important;
    color: yellow !important;
    }

}

/* The rest is just to extend the borders of some tables so there isn't empty space on the left */
@-moz-document url-prefix("http://tvtropes.org/pmwiki/topics.php"), url-prefix("http://tvtropes.org/pmwiki/conversations.php"), url-prefix("http://tvtropes.org/pmwiki/factivity.php"), url-prefix("http://tvtropes.org/pmwiki/factivity2.php"), url-prefix("http://tvtropes.org/pmwiki/profile.php"), url-prefix("http://tvtropes.org/pmwiki/voiceactivity.php"),  url-prefix("http://tvtropes.org/pmwiki/crown_activity.php"), url-prefix("http://tvtropes.org/pmwiki/crowner.php"), url-prefix("http://tvtropes.org/pmwiki/pm.php") {
 td {
    width: 0px !important;
    }
 span {
    color: yellow !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/conversations.php"), url-prefix("http://tvtropes.org/pmwiki/posts.php"), url-prefix("http://tvtropes.org/pmwiki/topics.php"){

div.container div div.pagetitle {
    display: none !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/conversations.php"){

/* zebra */
 tr:nth-child(2n+3) td {
    background-color: #303030 !important;
    }

/* Dimmer lines */
 tr td {
    border-color: #a2a2a2 !important;
    }

/* table title */
 th {
    background-color: #404040 !important;
    text-transform: capitalize !important;
    }

/* links */
 a:link {
    text-decoration: none !important;
    }
 a:visited {
    text-decoration: none !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/showcuts.php"){

/* zebra */
 div > div:nth-child(even) {
    background-color: transparent !important;
    }
 div > div:nth-child(odd) {
    background-color: #222 !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/CutList.php"){

/* The entry to cut box */
 div#wikiright {
    background-color: transparent !important;
    }

/* zebra */
 tr:nth-child(4n+3) td {
    background-color: #222 !important;
    }
 tr:nth-child(4n+4) td {
    background-color: #222 !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/posts.php"){

/* Expand the border */
 td {
    width: 0px !important;
    }

/* Forum number pages */
 div.forumpagebuttons {
    border-style:solid !important;
    border-color: #555 !important;
    border-width: 2px !important;
    background-color: #333 !important;
    }

 a.forumpagebutton {
    text-decoration: none !important;
    background-image: none !important;
    }

/* Forum headers */
 div.forumreplyheader  {
    background-color: #000 !important;
    color: #bbb !important;
    z-index: 2 !important;
    position: relative !important;
    }

/* Forum bodies and avatars */
 div.forumreplybody, div.forumimageholder {
    background-color: transparent !important;
    border-color: #444 !important;
    z-index: 2 !important;
    position: relative !important;
    }

/* Forum users' avatar caption */
 div.forumtitle {
    background-color: #222 !important;
    border-color: #444 !important;
    border-style: solid !important;
    border-width: 1px !important;
    color: #ddd !important;
    font-weight: bold !important;
    border-left: none !important;
    }

/* Forum users' signatures */
 div.forumsigline {
    border-color: #444 !important;
    color: #ddd !important;
    z-index: 2 !important;
    position: relative !important;
    }

/* Post counter */
 div.forumreplyheader sup {
    color: yellow !important;
    }

/* Time */
 div.forumreplyheader a:link span {
    color: yellow !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/ykttw.php"), url-prefix("http://tvtropes.org/pmwiki/discussion.php"){

/* YKTTW things */
 div.header, div.wtitleholder {
    background-color: #333 !important;
    color: #fff !important;
    }

 div#fauxtitle, div.newykttw, div.ykttwtext, div.levelOneCommands, div.levelThreeCommands {
    background-color: #111 !important;
    color: #fff !important;
    }

 .aYKTTW {
    background-color: #222 !important;
    border-color: #bbb !important;
    }

 div.replytext {
    background-color: #111 !important;
    border-color: #777 !important;
    color: #bbb !important;
    }

/* YKTTW span tags */
 div.levelOneCommands span, div.timestamp div {
    color: #F480FE !important;
    }
 div.timestamp span {
    color: #80C8FE !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/review_activity.php"){

/* zebra */
 tr:nth-child(odd) td {
    background-color: #222 !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/launches.php"){

/* zebra */
 tr:nth-child(even) td {
    background-color: #222 !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/factivity.php"), url-prefix("http://tvtropes.org/pmwiki/factivity2.php"){

/* zebra */
 tr:nth-child(even) td {
    background-color: #303030 !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/changes.php"){

 div#wikimiddle div {
    background-color: transparent !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/pmwiki.php"){

 div.footnav {
    display: none !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/pictureindex.php"){

 div td center table tr td>a {
    background-image: none !important;
    }

}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/pmwiki.php/Main"), url-prefix("http://tvtropes.org/pmwiki/ykttw.php"){

/* new plus icon */
 #wikileftpage h1 {
    background: url(data:image/png;base64,) 5px no-repeat !important;
    }
}

/*BigT's fix for the subpage links */ 
@-moz-document domain("tvtropes.org") {
	a.subpagelink, a.subpagelink:visited {
		color: #cccccc !important;
		text-decoration: none !important;
		background-color: #333333 !important;
		border: 1px solid #111111 !important;
	}
		/* making watch buttons match */
	div#watchmsg div.buttonframe, div#watchmsg div.buttoninterior,
	div.folderlabel,div.templatebuttoninterior,
	div.templatebuttonframe  {
		color: #cccccc !important;
		background-color: #333333 !important;
		border: 1px solid #111111 !important;
		-moz-border-radius: 4px !important;
		-webkit-boarder-radius: 4px !important;
	}
	div.buttoninterior a, div.buttoninterior a:visited {
		text-decoration: none !important;
		color: #cccccc !important;
	}
	div#watchmsg div.buttoninterior:hover, a.subpagelink:hover{
		border-color: limegreen !important;
	}
}
