css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {







#contentCol  { background-color:#7E354D !important; }

.profile .profile_color_bar  { background-color:#7E354D !important; }





/* navigation */

#blueBar       { background:#f0788c !important; }

#pageLogo a    { background:url(http://www.madebymat.com/facebook/icons.png) no-repeat scroll -21px 0 !important; }

#headNavOut    { background:#f0788c !important; border-color:#f0788c !important; }







/* main nav icons */

.jewelToggler  { background-image:url(http://www.madebymat.com/facebook/icons.png) !important; }

#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,

#jewelMail:hover, #jewelMail:focus, #jewelMail:active,

#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#f0788c !important; }

#jewelCase .jewel  { border:none !important; }







/* search */

#navSearch button  { background:url(http://www.madebymat.com/facebook/icons.png) no-repeat scroll -1px 0 !important; }

#navSearch     { border-color:#f0788c !important; }







/* nav tabs */

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#de264c !important; color:#ffffff !important; }







/* boxes */

.lightblue_box  { background-color:#f0788c !important; border-color:#f0788c !important; }

.commentable_item .ufi_section  { background-color:#f0788c !important; }

textarea, .inputtext, .inputpassword  { border-color:#ffffff !important; }







/* links */

a  { color:#ffffff !important; }

.uiLinkButton input  { color:#ffffff !important; }

.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#ffffff !important; }

button.as_link  { color:#ffffff !important; }







/* side nav */

.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#f0788c !important; }



.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#f0788c !important; }

.friends_online_sidebar li.buddy_row a:hover  { background-color:#f0788c !important; }



.unread  { background-color:#f0788c !important; }

.line  { color:#ffffff !important; }







/* pop up box  */

#jewelCase .jewelFooter a   { background-color:#f0788c !important; }

#jewelCase .jewelFooter .uiTextSubtitle  { color:#ffffff !important;  }



#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#ffffff !important; }



#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#fce1e8 !important; border-color:#ffffff !important; }

#jewelCase .jewelBox  { background-color:#f0788c !important; }







/* copy */

.uiHeader h2  { color:#f0788c !important; }







/* graffiti */

.current_stuff td  { background-color:#f0788c !important; border-color:#ffffff !important; }







/* profile page */

.UIComposer .UIComposer_Box  { background-color:#f0788c !important; border-color:#ffffff !important; }

.profile .box .box_header  { background-color:#f0788c !important; border-color:#ffffff !important; }







/* profile tabs */

.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#f0788c !important; }

.profile .top_bar ul.tabs li a.tab_link  { background-color:#f0788c !important; border-color:#f0788c !important; }

.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#f0788c !important; color:#ffffff !important; border-color:#de264c !important; }









/* chat */

#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#ffffff !important;  }

#chat_tab_bar .chat_header  { background-color:#f0788c !important; border-color:#ffffff !important; }

#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }

#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#f0788c !important; color:#ffffff !important; }

#presence_bar .presence_menu_opts h2 span  { background:#f0788c !important; }

#presence_bar .presence_menu_opts h2  { border-color:#ffffff !important; }

.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#f0788c !important; }



#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#f0788c !important; border-color:#ffffff !important; }

#presence .presence_bar_button .inner_button  { border-top:none !important; }

#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }



#presence .presence_menu_opts  { border-color:#f0788c !important; }







/* buttons */

.UIButton_Blue  { background:#f0788c !important; border-color:#ffffff !important; }

.UIComposer_InputArea  { border-color:#f0788c !important; }

.uiButtonConfirm  { background:#f0788c !important; border-color:#ffffff !important; }

#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }















}
