css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hotukdeals.com") {

body {
background-color: whitesmoke !important;
}

.text_deal_view {
border: 1px solid #d9d9d9 !important;
}

table.text_deal_view td {
padding:2px 0 2px 5px !important;
border-bottom:1px solid #D9D9D9;
}

.alternating_row {
background-color: #fff !important;
}

#statbox {
display: inline !important;
width: 150px !important;
}

#two_column_wrap {
padding-right:160px !important;
}


.text_title_new a {
font-weight: normal !important;
}

.text_title_old a {
font-weight: normal !important;
}

.text_title_new h2 a{
font-size: 0.8em !important;
margin-right:15px !important;
font-weight:bold !important;
}

.text_title_old h2 a{
font-size: 0.8em !important;
margin-right:15px !important;
}

td.last_posting {
width:170px !important;
}

.page_links {
margin-top: 5px;
margin-bottom: 10px;
}

.page_links a {
color:#3C5890 !important;
background-color:#FFF !important;
padding:1px !important;
margin-right:2px !important;
padding: 0 2px 0 2px !important;
border: 1px solid #D8E5FC !important;
text-decoration: none;
}

.page_links a:hover {
color:#3C5890 !important;
background-color:#D8E5FC !important;
padding:1px !important;
margin-right:2px !important;
padding: 0 2px 0 2px !important;
border: 1px solid #D8E5FC !important;
text-decoration: none;
}

.message_body {
background-color:#efefef !important;
}

.post_wrapper {
border: 1px solid #d9d9d9;
background-color: #d9d9d9;
}

.forum_quote_wrapper {
padding:0px 20px 10px 35px;
margin-left:0px !important;
background-image:none !important;
}

.forum_quote {
background-image:none !important;
background-color: whitesmoke !important;
padding:7px;
border: 2px inset #d9d9d9 !important;
font-size: 100% !important;
}

.last_post_link {
color: #123889 !important;
}

.deal_list {
background-color: #fff !important;
border: 1px solid #afafaf;
padding-left:10px !important;
}

#forum_stats h2 {
font-size: 1em !important;
font-weight: bold !important;
}

#forum_stats dt {
width: 150px !important;
text-transform: none !important;
font-style:italic;
color: #123889 !important;
}

#forum_stats dd {
margin-left: 0 !important;
}

#hottest_deals h2 {
font-size:1em !important;
font-weight:bold !important;
}

input#merchant_search {
width: 120px !important;
}

.merchant_filter h2 {
font-size: 1em !important;
font-weight:bold !important;
}

dl#hottest_deals dt {
font-size: 0.8em !important;
float: none !important;
}

dl#hottest_deals dd {
font-size: 0.8em !important;
padding-right: 5px !important;
}

.deal_current_temp_red_txt{
display:none !important;
}

.deal_current_temp_blue_txt{
display:none !important;
}

.active_list {
width: 130px !important;
overflow:hidden !important;
padding:0 !important;
}
.hottest_list {
width: 130px !important;
overflow:hidden !important;
padding:0 !important;
font-size:10px !important;
}
.sidebox-filter h2 {
font-size:10px !important;
font-weight:bold !important;
}
.voucher-search-txt {
width:130px !important;
margin-top:10px !important;
}
#hottest_deals h2{
font-size:10px !important;
}
#active_threads a{
font-size:10px !important;
}
#hottest_deals a{
font-size:10px !important;
}
.hottest_current{
font-size:10px !important;
}
td.forum_image {
width:50px !important;
}
.bw-list {
display:none;
}

#statbox div img {
display:none;
}

#statbox div br {
display:none;
}

#statbox div {
margin-top:0px !important;
}
#footer {
display:none;
}

.footer_box {
border:none !important;
}

.user_details_right div{
width: 170px !important;
overflow:hidden !important;
}
#forum_stats
{
display:none;
}
#widget-links
{
display:none;
}
#hukd-blogs
{
display:none;
}

.vbmenu_popup
{
border: solid 1px #000000 !important;
}
#widget-contest
{
display:none;
}

#statbox a img
{
/* removes deals pwn */
display:none;
}
}
