css:
@-moz-document  url-prefix( http://www.ejustice.just.fgov.be/wet/wet.htm ),
                url-prefix( http://www.ejustice.just.fgov.be/loi/loi.htm )
                
{


:root
{
	overflow-y: scroll !important;
}

body
{
	/*overflow: scroll;
*/
}

:link
{
	color: #203E9F;
}

#searchDiv, #resultsDiv, #textDiv, #infoDiv
{
	display  : block;
	width    : 99%;
	height   : 93%;
	margin   : 1px;
	padding  : 5px;
	border   : 0px black solid;
	overflow : auto;
}

#topDiv
{
	margin     : 1px;
	height     : 22px;
	width      : 99%;
	border     : 0px black solid;
	padding    : 0px;
	overflow-x : hidden !important;
}

#tabDiv
{
	float  : left;
	width  : 50%;
	height : 20px;
}

#tabDiv:link
{
	text-decoration: none;
}

#navDivText, #navDivList
{
	float      : right !important;
	height     : 22px;
	width      : 50%;
	text-align : right;
	padding    : 0px;
	margin     : 0px;
}

#navDivText *, #navDivList *
{
	padding  : 0px;
	margin   : 0px;
}

#navDivText table, #navDivList table
{
	float    : right;
	width    : 100px !important;
}

#hiddenFrame
{
  visibility : hidden;
}

}

@-moz-document  url-prefix( http://www.ejustice.just.fgov.be/cgi_loi/loi_a1.pl ),
                url-prefix( http://www.ejustice.just.fgov.be/wet/wet.htm ),
                url-prefix( http://www.ejustice.just.fgov.be/loi/loi.htm )

{


body
{
	margin           : 0px !important;
	padding          : 10px !important;
	background-color : #FFFFFF;
}

:link
{
	color : #193285 !important;
}

.startDateAnnotation
{
	font-weight  : 500 !important;
	font-size    : 12px !important;
	color        : #800010 !important;
}

.HeaderDivs
{
	background-color : #55585F;
	padding          : 4px;
	margin-top       : 10px;
}

.headerLINKS
{
	background-color : #55585F;
	color            : #f8f8f8 !important;
	font-family      : monospace;
	text-decoration  : none !important;
	font-size        : medium;
	font-weight      : bold;
}

.headerLINKStext
{
	background-color : #55585F;
	color            : #f8f8f8 !important;
	font-family      : Verdana, Helvetica, sans-serif;
	text-decoration  : none !important;
	font-size        : small;
	font-weight      : bold;
}

/* TOPTABLE ========================================================================================================================================*/

#topHeaderDiv
{
	margin : 0px 0px 20px 0px;
}

#topHeaderDiv *
{
	background-color   : #55585F;
	font-weight        : normal !important;
	font-size          : x-small;
	color              : #FEF5C5 !important;
}

#topHeaderDiv b
{
	font-weight    : bold !important;
	color          : #f8f8f8 !important;
	margin-right   : 2px;
}

.topLinks
{
	float          : left;
	clear          : none;
	margin-right   : 5px;
	padding        : 5px;
	font-size      : small !important;
	border-top     : 1px solid #FEF5C5;
	border-left    : 1px solid #FEF5C5;
	border-bottom  : 2px solid black;
	border-right   : 2px solid black;
}

#langLinks
{
	float      : right;
	clear      : none;
	padding    : 5px;
	font-size  : small !important;
	border     : 1px solid #FEF5C5;
}

/* TITLETABLE =========================================================================================================================================*/

#titleTable
{
	margin-top : 0px;
	padding    : 0px 6px 0px 6px;
}

#titleTextP
{
	font-family  : Palatino Linotype, High Tower Text, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size    : medium;
}

#titleTable p
{
	margin-bottom  : 8px;
	padding        : 0px;
}

.titleInfoTable
{
	border : 0px;
}

.titleInfoCell1
{
	border         : 0px;
	color          : #872D1D;
	font-weight    : bold;
	vertical-align : top;
	white-space    : nowrap;
}

.titleInfoCell2
{
	border : 0px;
}

/* TOCTABLE ===========================================================================================================================================*/

#tocTable
{
	border     : 1px solid #55585F;
	border-top : none;
	padding    : 5px;
}

.tocTitles
{
	margin-top     : 10px;
	font-variant   : small-caps;
}

.tocTexts
{
	margin-top   : 5px;
	margin-left  : 15px;
}

/* TEXTTABLE ==========================================================================================================================================*/

#textTable
{
	border       : 1px solid #55585F;
	border-top   : none;
	padding      : 5px;
	padding-top  : 15px;
}

#textTable p
{
	margin         : 0;
	margin-bottom  : 8px;
	padding        : 0;
	text-align     : justify;
	font-family    : Palatino Linotype, High Tower Text, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size      : medium;
}

.ArticleTitle
{
	padding        : 4px;
	margin-bottom  : 5px;
	margin-top     : 5px;
	border         : 1px solid #ffc47d;
	clear          : none;
}

.ArticleTitle, .ArticleTitle *
{
	background-color : #F9F9F9 !important;
	color            : #353535 !important;
	font-weight      : normal;
}

.ArtTitleText
{
	float    : left;
	clear    : none;
	margin   : 0px;
}

.origLinkDiv, .jumpArtArrows
{
	float        : right;
	margin-right : 10px;
}

.ArtArrImg
{
	border : none !important;
	margin : -1px 0px -3px 0px;
}

.ArticleOriginal
{
	background-color : #F5F5FF !important;
	padding          : 5px;
	font-weight      : bold;
	display          : none;
}

.ArticleOriginal *
{
	background-color : #F5F5FF !important;
}

.ArticleOriginal:link
{
	color  : #0000EE !important;
}

.mainContentTitleDivs
{
	margin-top     : 0px;
	margin-bottom  : 20px;
	font-variant   : small-caps;
	font-weight    : bold;
}

.mainContentArticleDivs
{
	margin-top     : 0px;
	padding-bottom : 30px;
	margin-bottom  : 0px;
}

.mainContentQuoteDivs
{
	margin-left    : 30px;
	margin-right   : 30px;
}

.mainContentQuoteDivs + .mainContentQuoteDivs
{
	margin-top : 30px;
}

.mainContentQuoteDivs *
{
	color        : #54595F;
	font-style   : italic;
}

.mainContentNonProcessedDivs
{
	margin-top     : 20px;
	margin-bottom  : 10px;
	font-family    : courier new !important;
	font-size      : small;
}

.numDegree, .numDot, .letterBracket, .numBracket, .numParaf, .letterDot, .romanNumBracket, .romanNumDot, .numSemiColon
{
	margin-left    : 10px;
	margin-bottom  : 15px;
}

.numDegree p, .numDot p, .letterBracket p, .numBracket p, .numParaf p, .letterDot p, .romanNumBracket p, .romanNumDot p, .numSemiColon p, ul p
{
	margin-bottom  : 0px !important;
}

.sumIntroCell
{
	white-space    : nowrap;
	font-weight    : bold;
	font-family    : Palatino Linotype, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size      : medium;
	vertical-align : top;
}

/* PREAMBULETABLE =====================================================================================================================================*/

#preambuleTable
{
	display    : none;
	border     : 1px solid #55585F;
	border-top : none;
	padding    : 5px;
}

#preambuleTable p
{
	margin         : 0;
	margin-bottom  : 8px;
	padding        : 0;
	text-align     : justify;
	font-family    : Palatino Linotype, High Tower Text, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size      : medium;
}

/* ROYALREPORTTABLE ===================================================================================================================================*/

#royalReportTable
{
	display    : none;
	border     : 1px solid #55585F;
	border-top : none;
	padding    : 5px;
}

#royalReportTable p
{
	margin         : 0;
	margin-bottom  : 8px;
	padding        : 0;
	text-align     : justify;
	font-family    : Palatino Linotype, High Tower Text, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size      : medium;
}

/* MODTABLE ===========================================================================================================================================*/

#modTable
{
	display      : none;
	border-color : #55585F !important;
	border-top   : none;
   width : 100%;
}

#realModTable
{
  width : 100%;
}

#modTable td
{
	text-align : center;
}

#modTableHeaderDiv
{
	padding  : 4px 5px 1px 4px;
}

#modTableHeaderLinkDiv
{
	float    : left;
	margin   : 0px;
	padding  : 0px;
}


/* WORKONBYTABLE ======================================================================================================================================*/

#workOnByTable
{
	display    : none;
	border     : 1px solid #55585F;
	border-top : none;
	padding    : 5px;
}

#workOnByTablep
{
	margin         : 0;
	margin-bottom  : 8px;
	padding        : 0;
	text-align     : justify;
	font-family    : Palatino Linotype, High Tower Text, Garamond, Baskerville Old Face, Bookman Old Style, Book Antiqua, Times New Roman;
	font-size      : medium;
}

}/* end of @-moz-document url */
