css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mmm-tasty.ru/main") {
body{
  background-color:#111!important;
  background-image:none !important;
}
.rounded_main_center{
  padding-top:10px !important;
  -moz-border-radius:0 0 30px 30px!important;
}
/*Header*/
.logo {display:none;}
.top_hello{
  color:#fff !important;
  padding:15px 0!important;
}
.top_hello A.no_visited:link,
.top_hello A.no_visited:visited{
  color:#fff !important;
  text-decoration:none !important;
  padding:0!important;
}
.top_hello A.no_visited:hover,
.top_hello A.no_visited:focus{
  color:#fff !important;
  text-decoration:underline !important;
}

.register_enter{margin-bottom:0 !important}
.top_menu{padding-top:0!important}
.top_menu UL{
  background:#fff;
  padding:15px 8px!important;
  margin:0px auto!important;
  width:516px;
  -moz-border-radius:20px 20px 0 0!important;
  border-bottom:1px solid #EEEEEE !important
}
.top_menu UL LI{
  margin:0 5px !important;
}
.top_menu A:link,
.top_menu A:visited{
  color:#FF0412!important;
  text-decoration:underline !important;
  background:transparent !important;
}
.top_menu A:hover,
.top_menu A:focus{
  color:#fff!important;
  text-decoration:none !important;
  background:#666!important;
  -moz-border-radius:4px!important;
}
.top_menu A.selected:link,
.top_menu A.selected:visited{
  color:#fff!important;
  text-decoration:none !important;
  background:#333!important;
  -moz-border-radius:4px!important;
}
.top_menu A.selected:hover,
.top_menu A.selected:focus{
  background:#666!important;
}

/*sidebar*/
.sidebar {left:-120px !important;}
.sidebar_right {left:607px !important;}
.sidebar .content{
  width:200px !important;
}
.sidebar P,
.sidebar H2,
.sidebar A,
.sidebar SPAN{
  color:#fff !important;
  background:transparent  !important;
}

.main_page .sidebar H2,
.sidebar H2{
  background:transparent !important; 
  font-size:1.4em !important;
  line-height:1.4em !important;
  padding:2px 0 0 !important;
  margin:0 0px 0px !important;
}
.sidebar H2 span,
.sidebar H2 A{color: #fff!important;}
.sidebar_right H2{
  padding:2px 12px 0 !important;
  margin:0 0px 0 0px !important;
}
.sidebar_right .sidebar_search FORM{padding-top:10px  !important}

.sidebar #feedback_create {padding-top:20px }
.sidebar #feedback_create FORM{padding-top:10px  !important}

.sidebar .users_nav A.highlight,
.sidebar .vertical_nav  A.highlight{
  text-decoration:none;
  font-weight:bold
}
.sidebar .users{
  padding-top:10px  !important
}
.post_author a{
  background:#ccc !important; 
  color: #333!important
}
/*post types*/
.onair IMG{max-width:100%}
DIV.post_body h1{margin:0 !important}
/*regular*/
.post_text{
  border-left:0 none  !important;
  padding-left:0 !important;
}

}
