css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

.left-column.match-body-height {
width: 40% !important;
margin-right: -1px;
/* margin-right -1px 
is a bug fix by accidental discovery, 
for some reason it fixes the 
.right-column.mach-body-height  bug */
}
.right-column.match-body-height {
width: 60% !important;
margin-left: -1px;
}
#notes .notes LI.current-note {
background: #f86624 !important;
}
#notes .notes LI.current-note:hover {
background: #d6b8b0 !important;
opacity: 0.8;
}
#notes .notes LI.current-note .subject {
font-size: 20px !important;
}
#notes .notes LI.current-note .note-preview {
visibility: hidden;
}
#notes .notes LI.current-note .note-preview:before {
content: '*Note open->->->';
visibility: visible;
font-weight: bold;
}
#current-folder .notes {
padding: 3px !important;
background-color: #313168;
border: solid #000000 1px;
-moz-border-radius:  3px;
}
#current-folder .notes li {
margin: 1px;
border: solid 1px #000000 !important;
border-top: solid 2px #000000 !important;
margin-bottom: 2px;
-moz-border-radius:  4px;
background: #a6ddb6 !important;
}
#notes UL.notes LI.unread {
background: #ff2636 !important;
}
#notes UL.notes LI.unread:hover {
background: #ff3646 !important;
}
#current-folder .notes li:hover {
margin-bottom: 3px;
margin-left: 2px;
margin-right: 2px;
border: solid 1px #000000 !important;
-moz-border-radius: 4px;
background: #d0d1d1 !important;
}
#current-folder .notes li a {
width: 100%;
}
#current-folder .notes li .sender {
text-transform: capitalize;
}
#notes span.sel-arrow i {
display: none !important;
}
#notes .l {
color: #f1f1f1 !important;
}
#notes .r {
color: #f1f1f1 !important;
}
/*notes for groups, start from this point onwards*/
UL#notes_list {
padding: 3px !important;
background-color: #313168;
border: solid #000000 1px;
-moz-border-radius:  3px;
}
UL#notes_list LI.f.note.current-note {
background: #f86624 !important;
}
UL#notes_list LI.f.note.current-note:hover {
background: #d6b8b0 !important;
opacity: 0.8;
}
UL#notes_list LI.f.note {
margin: 1px !important;
border: solid 1px #000000 !important;
border-top: solid 2px #000000 !important;
margin-bottom: 2px !important;
-moz-border-radius:  4px;
background: #a6ddb6 !important;
}
UL#notes_list LI.f.note:hover {
margin-bottom: 3px !important;
margin-left: 2px !important;
margin-right: 2px !important;
border: solid 1px #000000 !important;
-moz-border-radius: 4px;
background: #d0d1d1 !important;
}
UL#notes_list LI.f.note.unread-note {
background: #ff2636 !important;
}
UL#notes_list LI.f.note.unread-note:hover {
background: #ff3646 !important;
}
UL#notes_list LI.f.note.current-note .note-preview {
visibility: hidden;
}
UL#notes_list LI.f.note.current-note .note-preview:before {
content: '*Note open->->->';
visibility: visible;
font-weight: bold;
}
UL#notes_list li a.g {
width: 100%;
color: #000000 !important;
}
UL#notes_list li .sender {
text-transform: capitalize;
}
UL#notes_list I.sel-arrow {
display: none !important;
}
UL#notes_list .l {
color: #f1f1f1 !important;
}
UL#notes_list .r {
color: #f1f1f1 !important;
}
UL#notes_list LI.hr {
display: none;
}
}
