css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mmo-champion.com") {
/* ### - Removing Ads - ### */
#page-cover, #ad-horizontal-header, .recent-ad, #ad_global_above_footer, .bluetracker #leaderboard_top:first-child, .mpu_advertisement, #ad_board_after_forums, #ad_forum_below_threadlist {
display:none !important;
}
/* ### - Removing Background Ads - ### */
body {
background: url("http://static.mmo-champion.com/images/tranquilizing/bg.jpg") repeat-x scroll 0 0%, url("http://static.mmo-champion.com/images/tranquilizing/bg_repeat.jpg") repeat scroll 0 0 #44552B !important;
}
/* ### - Moving Recent Boxes - ### */
.site-mmo-champion #page .body_wrapper .vbcms_content #column1 .bar .border, #ipbwrapper #site .bluetracker .recentbar {
margin-right: 0px !important;
float: none !important;
width: auto !important;
}
#ipbwrapper #site .bluetracker .recentbar .recent {
width: 48.8% !important;
}
/* ### - Replacing Logo Image - ### */
img[src="http://static.mmo-champion.com/images/tranquilizing/logo.png"] {
width: 0px;
height: 90px;
padding-left: 179px;
background-image:url('http://lanjelin.com/images/mmo-champ6.png') !important;
}
/* ### - Main Container - ### */
.site-mmo-champion, .site-mmo-champion #page {
padding-bottom: 45px !important;
margin-top: 35px !important;
margin-left: auto !important;
margin-right: auto !important;
width: 84% !important;
max-width: 10240px !important;
}
/* ### - Curse Bar Top - ### */
.site-mmo-champion #netbar {
margin-left: 8% !important;
width: 84% !important;
/*margin-top: -35px !important;*/
}
#ipbwrapper #netbar {
margin-left: 19.9% !important;
width: 58% !important;
margin-top: 35px !important;
/*margin-bottom: 35px !important;*/
}
/* ### - Footer - ### */
.site-mmo-champion #board_footer {
width: 84% !important;
}
#ipboard_body #board_footer{
margin-bottom: 35px !important;
}


/* ### - Ignore This - ### */
/*
-moz-border-radius: 15px 15px 15px 15px !important;
-webkit-border-radius: 15px 15px 15px 15px !important;
*/



}
