css:
/* Custom dAv7.1 header skin excusively for Yumi-iko by AwsumZ :: http://awsumz.deviantart.com/ :: */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* ------------- deviantART logo and main background of the header ------------- */

body, #overhead, #oh1 {
background-image: url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/top-2.png)!important;
background-repeat: repeat-x;
border-right:#fff 1px dotted!important;
}

#overhead {
background:url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/img2-1.png) repeat-x!important;
}

/* ------------- the darker version of your header when you hover over with the mouse ------------- */

.mmhover .oh-l, .mmhover2 .oh-l, .mmhover3 .oh-l, .oh-bl .active, .oh-m:active {
background-color: transparent;
background-image:url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/img3-1.png)!important;
background-repeat: repeat-x;
}

/* ------------- the arrow image the main drop-down menu next to the dA logo ------------- */

#oh-more {
background:url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/arrow-1.png) no-repeat left!important;
margin-top:3px!important;
}

/* ------------- the border/divider for the rest of the menus ------------- */

.oh-ml {
border-right:#fff 1px dotted!important;
}

/* ------------- this removes the original dA arrow image from showing - do NOT edit this! ------------- */

.oh-mg {
display:none!important;
}

/* ------------- text inside the drop-down menus ------------- */

.mi, .oh-m-surfer {
color: #f4f0f8!important;
}

/* ------------- text on hover inside the drop-down menus ------------- */

.mi:hover, .oh-m-surfer:hover, .oh-menu a.active {
color: #6717ad!important;
background: #f4f0f8!important;
}

/* ------------- background colour for collections and friendslist ------------- */

.oh-menu, #overhead-collect #lub, .popup2-click-friends {
background:#6717ad!important;
}

/* ------------- dividers inside drop-down menus ------------- */

.oh-hr {
border-top-width: 1px;
border-top-style: solid;
border-top-color: #36095e!important;
background-color: #a78cbf!important;
}

/* ------------- the word 'Deviant' (or 'Admin') and symbol in front your username ------------- */

.oh-darker {
color: #caa7ec!important;
}

/* ------------- darker text inside menus (such as 'Manage Deviations') ------------- */

.oh-menu .oh-darker, .oh-menu .oh-darker a {
color: #caa7ec!important;
}

.oh-menu .oh-darker:hover, .oh-menu .oh-darker a:hover {
color: #6717ad!important;

}
}
