css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("demi.fi") {


/* poistetut navigaation linkit - jos haluat jotain takaisin näkyviin, poista se tästä */
.navi-blogit, .navi-vastaukset, .navi-frendit, .navi-megamokat, .navi-neroudet, .navi-testit, .navi-tv, .navi-kilpailut, .navi-apua
{
 display: none !important; 
}

/* Piilotettavat jutut. */
#logo, .latest-status-updates, .promotedGroup, #InternetTVDiv, .bgImage.fLeft.full-width,
 #header-right, #banner-top, #Advertisement, #panorama_ad, #blogi-nostot,  .form_apua_text_box, .footer-logo, .banner-notification, .forum-demila-lift, .forum_demila_lift,  .commercial_box, .commMiddleImage, .latestComp, .commercial_hor, .commercial_hor3, .navi_main, #demi-facebook, #banner1rightloader,
DIV.fLeft.mb-20.full-width.center, H1.hdrUpper.fLeft.full-width.p-0,
DIV.mb-10.fLeft.full-width.center
{
 display: none !important; 
}

/* teksteihin ja väreihin vaikuttavia */
.user-actions a, .bLink, .listing a{ color: #333333  !important;}
a:hover{ color: #888888  !important;
text-decoration: underline;}
.button, .linkBut3{ color: #ffffff !important;
text-decoration: underline;}


/* värjää "osallistu keskusteluun" -laatikon */
.addPostForm {
 background-color: #ffffff !important;
border: solid 1px #d2d2d2 !important;
}

/* värjää keskustelun 1. lukemattoman viestin*/
.box-1Post{
 background-color: #f1f1f1 !important;
}

/* oikean sivupalkin väri */
.K_,  #right_part{
 background-color: #C0C0C0 !important;}
h2.hdrUpper.fLeft.full-width, h2.hdrUpper{ background-color: #DCDCDC  !important;}
.fillStar, .borderStar{ background-image: none  !important;}

/* Hakuboxi */
#search.fLeft {
	background:#FFFFFF url(http://i54.tinypic.com/k4idcg.jpg) no-repeat 4px 4px;
	padding:4px 4px 4px 22px;
	border:1px solid #CCCCCC;
	width:230px;
	height:18px;
-moz-border-radius: 0.5em !important;
}
#query{ 
background-color: #ffffff !important;
}
.arrow-button {
display: none !important;}
#search.fLeft{
position: absolute;
top: 100px;
right: 5px;
width: 150px;
height: 20px; }
#search.fLeft a{ 
font-size: 10px !important;
padding-top: 5px !important;
position: absolute;
right: 0;
top: 25px;}


/* mm. liikuttavat kirjautumisjuttuja*/
.user-actions {
padding-left: 50px !important;
width: 100%;
height: 155px !important;
}
.user-actions a{
color: gray !important;
}
.user-actions ul{
padding-top: 80px !important;
}
#login{
padding-left: 20px !important;
}


/* navigaatio siirtyy oikealle ylös */
#navigation{
 background-color: white !important;
 padding-top: 10px;
 position: absolute !important;
 right: 5px;
}

/*varjostuksia, liikuttelua yms.*/
#container{
padding-top: 14px !important;
 position: relative !important;
border: solid 1px #d2d2d2 !important;
-moz-border-radius: 1em !important;
height: 175px !important;
background-color: white;
}
#contentHolder{
border: solid 1px #ffffff !important;
border-top: 0 !important;
-moz-box-shadow: 0px 10px 15px 10px rgba(0,0,0,.1) !important;
}

/*etusivua varten*/
.fLeft#mydemi_center{
display: none !important;
}
}
