css:
@-moz-document url-prefix('http://www.thehelper.net/') {


/*Background*/
body{background-color:#000000!important;}

/*
body{background-image:url('http://homepages.physik.uni-muenchen.de/~georg.hennig/artificial_stars_m35.jpg') !important; background-repeat: repeat !important;
background-attachment: fixed !important;}
*/


/*Text*//*
td[align=right]
{text-align : right !important; 
width:300px !important; 
font-size: 11px !important; 
font-family: arial !important;
color: #999 !important;}
*/
/*:link,:visited { color: #eef !important;}*/
a:link{color:#eef!important;}
a:visited{color:#bbc!important;}
a:link:hover{color:#cccccc!important;}
a:visited:hover{color:#afafaf!important;}

#vB_Editor_QR_textarea {color: black !important;}


/*colors*/
*,[color="#E1E4F2"]
{color:#ccc !important;
background-color: transparent !important;}

[background-color="white"]
{background: black !important; color: white !important;}

/*
[color="rgb(34, 34, 156)"]{color: green !important;}
[color="#22229C"]{color: green !important;}
*/


/*tables/colors*/
table,[color="#D1D1E1"]
{background-color: transparent !important;}

td,[color="#F5F5FF"]
{background-color: transparent !important;}


td[class="alt1"]{color: #e1e1e1 !important}
/* -------------- */ /*
td,[color="#000000"]
{color: #e1e1e1 !important}
*/ /* -------------- */



td[class="tcat"]{color: #ddd !important;
background-image: none !important;
background-color: transparent !important;
border: none !important;
font-size: 16px !important;}

div[class="panel"]{background: transparent !important}


div[class="vbmenu_popup"]{background-color: black !important;
border: 1px solid rgb( 90, 90, 90 )!important;}



tr,[background-color="#869BBF"]{background-color: transparent !important;
background-image: none !important;}
td,[width="100%"]{background-color: transparent !important;
background-image: none !important;}


table[cellpadding="6"]{border: 1px solid rgb( 50, 50, 50 )!important;}


/*sidebar*/
TD[header_right_cell]{display: none !important;}


/*facebook*/
td[class="fbconnect"]
{display: none !important;}
img[src="http://static.ak.fbcdn.net/images/loaders/indicator_white_small.gif"]{display:none!important}


/* SiteNavication */
TD[width="160"]{display:none;}


/*Top panel*/
table,div[class="alt1"]{color: #efefef}
td[class="panelsurround"]{background: transparent !important}

td[class="vbmenu_control"]
{color: #ddd !important;
background-color: transparent !important;}



/* no forum skins */

td[class="tfoot"] {display: none !important}

div[class="tborder"] { opacity: .5 !important; }
div:hover[class="tborder"]{ opacity:.75 !important; }
}

/* Forum Jump */
[name="f"]{color: black !important; background: white;}
[name="f"]:hover{color: white !important; background: transparent;}
option[selected="selected"]
{background-color: transparent !important; color: #a8a8a8 !important;
font-weight: 600 !important;}

.fjdpth0{background: transparent !important; color: #ffcece !important;
font-weight: 600 !important;}

/*Posting Rules*/
#collapseobj_forumrules {display: none !important;}
.tborder[width="210"] {display: none !important;}


/* border */

/*tr,*/[class="tborder"]{border: 1px solid rgb( 90, 90, 90 )!important;}

/* time */
[class="time"]{color: #C6C6C6 !important; font-weight: 600 !important;}
[class="time"]:hover{color: #EDBBBB !important; font-weight: 900 !important;}

/* code/triggers */

ul[class="thead block_row block_title list_no_decoration floatcontainer"],

#visitor_messaging,#visitor_messaging_tab,
.tborder tcat,#aboutme_tab,#stats_tab,#friends_tab,#contactinfo_tab,
#AceHartMaps_tab,.tborder thead overflow_tab,

div[class="wc3"]{color: #9FAD9C !important;
background: transparent !important;}

[class="alt2"]{background-color: transparent !important;
color: #D7E2D5 !important;}

/* vB *//*

td[id="vB_Editor_001"]{background-color: rgb(225, 225, 226) !important;}
div[id="vB_Editor_001_size_out"],div[id="vB_Editor_001_font_out"]{color: black !important;}

*/


/*
[width="95%"]{background-color: white !important;}
td[colspan="15"]{color: black !important;}
*/

[name="vbform"]{color: red !important;}


/* Profile */

[class="tborder tcat"],[class="tborder thead"],div[class="tborder content_block"]
{border: 1px solid rgb( 90, 90, 90 )!important;
background-color: transparent !important;}


div[class="tborder tcat tab_header"],div[id="visitor_messaging"],ol[id="message_list"]
{background-color: transparent !important; border: none !important; padding: 0px !important;}

div[class="alt2 smallfont"],div[class="tborder info_bar"]{border: none !important;}

[background-color="rgb(92, 112, 153)"]
{color:#ccc !important;
background-color: transparent  !important;
background-image: none !important;}

/*[id="moderation_select_comments"]{ background-color: black !important; Color: white !important;}*/

li[dir="ltr"] {border: 1px solid rgb( 90, 90, 90 )!important;
background: transparent !important;}
li[dir="ltr"]:hover {border: 1px solid rgb( 120, 120, 120 )!important;}

.tab_list>*:hover {border: 1px solid rgb( 150, 150, 150 )!important}

#moderation_select_comments{color: black !important; background: white;}
#moderation_select_comments:hover{color: white !important; background: transparent;}
option[selected="selected"]
{background-color: transparent !important; color: #a8a8a8 !important;
font-weight: 600 !important;}


#visitor_message_counters{background: transparent !important; opacity: .65 !important;}


h4[class="thead block_title"]
{background: transparent !important;}

#collapseimg_stats_mini,#collapseimg_friends_mini,#collapseimg_albums,#collapseimg_groups,#collapseimg_visitors
{background: transparent !important; opacity: .5 !important;}
#collapseimg_stats_mini:hover,#collapseimg_friends_mini:hover,#collapseimg_albums:hover,#collapseimg_groups:hover,#collapseimg_visitors:hover
{background: transparent !important; opacity: .75 !important;}


@-moz-document url-prefix('http://www.thehelper.net/') {

#menu {border: 1px solid rgb( 90, 90, 90 )!important;
background: black !important; color: #eee !important;}

#menu > *:hover{color: #f7f7f7 !important;}
}

#inlinemodsel_menu {border: 1px solid rgb( 90, 90, 90 )!important;
background: black !important; color: #eee !important;}

#inlinemodsel_menu > *:hover {color: #f7f7f7 !important;}


.sgicon {opacity: .90 !important;}
.sgicon:hover {opacity: 1 !important;}




/*SD-DAken (http://userstyles.org/styles/15401)
Andreas Jung (sd-daken.deviantart.com)*/

@-moz-document url-prefix('http://www.thehelper.net/') {
  input:not([type="submit"]):not([type="reset"]):not([type="button"]):not([type="checkbox"]):not([type="radio"]),
  textarea {
    -moz-appearance: none !important;
    -moz-box-sizing: padding-box !important;
    position: relative;
    left: -4px;
    border-width: 4px !important;
    -moz-background-clip: padding !important;
    -moz-border-bottom-colors: transparent transparent transparent rgb(120,120,120);
    -moz-border-left-colors: transparent transparent transparent rgb(120,120,120);
    -moz-border-right-colors: transparent transparent transparent rgb(120,120,120);
    -moz-border-top-colors: transparent transparent transparent rgb(120,120,120);

  }

}



@-moz-document url-prefix('http://www.thehelper.net/') {


/* Buttons */
button,.button,
input[type="submit"]
{color: black !important;}


#vB_Editor_QR_controls {background: #aaa !important; opacity: .5 !important;}


/*Logo*/
img[src="images/headermain.gif"]{background: url(http://img197.imageshack.us/img197/1818/thehelperlogot1.png) no-repeat !important;
height: 0 !important;
width: 0 !important;
padding-left: 434px !important;
padding-top: 103px !important;}
	

#vB_Editor_001_controls{background: white !important; opacity: .5 !important;}


/*Bars, Search, Groups*/
div.thead,
div[class="tcat block_title"]
{background: transparent !important;
border: none !important; }

div[class="social_group tborder content_block"]
{background: transparent !important;
border: 1px solid rgb( 90, 90, 90 )!important;}


@-moz-document url-prefix('http://www.thehelper.net/') {

img[src="images/headermain.gif"]
{background: 

url(http://img197.imageshack.us/img197/1818/thehelperlogot1.png) 

no-repeat !important;
height: 0 !important;
width: 0 !important;
padding-left: 434px !important;
padding-top: 103px !important;
opacity: .5 !important;}
	

img[src="images/headermain.gif"]:hover
{background: 

url(http://a.imageshack.us/img134/7231/thehelperlogot2.png) 

no-repeat !important;
height: 0 !important;
width: 0 !important;
padding-left: 434px !important;
padding-top: 103px !important;
opacity: .6 !important;}

}

}
