css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("furaffinity.net"), domain("www.furaffinity.net") {

/*--- Text size/style modifications ---*/

html
    { font-size: 100.01% !important; }

*
    { font-family: sans-serif !important; }

table,
tr,
td,
th
    { font-size: 10px !important; } /* Main Text Size */

/*--- Text color modifications ---*/

table,
tr,
td,
th
    { color: #fff !important; } /* Main Text Color */

a:link,
a:visited,
.no_overflow
    { color: #fff !important; }

.navigation a,
#submission .prev,
#submission .next
    { color: #41526B !important; }

a:hover
    { color: #cfcfff !important; text-decoration: none !important; }

/*--- Two-column contact information section ---*/

.websites
    { clear: none !important; }

/*---  Kill overflowing comments ---*/

td.replyto-message
    { max-width: 900px !important; overflow: hidden !important; }

.replyto-message *
    { color: inherit !important }

/*--- Fix space next to avatar in comments ---*/

th[width="20"],
td[width="20"]
    { display: none !important }

/*--- Improve notification area ---*/

.date-divider
    { margin-top: 5px !important }

div#messagecenter-submissions ul.messages-stream li
    { height: 190px !important }

/*--- Reducing page width ---*/

div#gallery ul.messages-stream li,
div#scraps ul.messages-stream li,
div#favorites ul.messages-stream li,
div#browse ul.messages-stream li,
div#search ul.messages-stream li
    {
    margin-left: auto !important;
    margin-right: auto !important;
    width: 150px !important;
    }

/*--- Hide deleted submissions ---*/

#favorites li:not([id]) { display: none !important }

/*--- Fix the right margin ---*/

span.reply-link
    { right: 0 !important; }

span.reply-link a
    { margin-right: 0px !important; }

/*--- Administration Notice box width ---*/

table#admin_notice_do_not_adblock
    { width: 100% !important; }

}
