css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.nicovideo.jp/my/channel"),
               url-prefix("http://www.nicovideo.jp/my/community") {
  .content_672_solo > .mb8p4:not(:first-child),
  /* å‹•ç”»æ•°ã€ãƒ¡ãƒ³ãƒãƒ¼æ•°ã‚’è¡¨ç¤ºã•ã›ãŸã‘ã‚Œã°æ¬¡ã®è¡Œã®ã‚’å‰Šé™¤ */
  .content_672_solo h3 + p.font12,
  /* æ–°ç€æƒ…å ±ã‚’è¡¨ç¤ºã•ã›ãŸã‘ã‚Œã°æ¬¡ã®è¡Œã‚’å‰Šé™¤ */
  .content_672_solo h3 + p.font12 + table,
  .content_672_solo h3 + p.font12 + table > tbody > tr > td[style]:first-child {
    display: none !important;
  }

  .content_672_solo {
    letter-spacing: -1em !important;
  }

  .content_672_solo > * {
    letter-spacing: normal !important;
  }

  .content_672_solo > table:not(.pager) {
    display: inline-block !important;
    vertical-align: top !important;
    width: 20% !important;
  }

  .content_672_solo > table:not(.pager) > tbody,
  .content_672_solo > table:not(.pager) > tbody > tr,
  .content_672_solo > table:not(.pager) > tbody > tr > td {
    display: block !important;
    width: 100% !important;
  }

  .content_672_solo > table:not(.pager) > tbody > tr > td {
    text-align: center !important;
  }

  .content_672_solo h3 + p.font12,
  .content_672_solo h3 + p.font12 + table {
    margin: 0 4px 2px !important;
  }

  .content_672_solo h3 + p.font12 > nobr:not(:only-child):last-child::before {
    content: "\a";
    white-space: pre;
  }

  .content_672_solo h3 {
    font-size: 12px !important;
    line-height: 1.375 !important;
    margin-bottom: 4px !important;
  }

  .content_672_solo h3 + p.font12,
  .content_672_solo h3 + p.font12 + table > tbody > tr > td.font12 {
    font-size: 10px !important;
    line-height: 1.25 !important;
  }

  img.img_sq64 {
    width: 96px !important;
    height: 96px !important;
  }
}
