css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eq2.eqtraders.com") {

/* ***************************************************** */
/* Alternate stylesheet for EQ2 Traders */
/* by Seagoat! */
/* http://eq2.lunaclick.net */
/* ***************************************************** */

a, a:link, a:visited, a:active {
    color: #9e0600!important;
    font-weight: bold!important;
    text-decoration: underline!important;
}

a:hover {
    text-decoration: none!important;
}

blockquote {
    color: #333!important;
    font-style: italic!important;
}

body {
    background: #606048 url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/main_bkgd01b.jpg') top center repeat-y!important;
    text-align: center!important;
    font: bold 10pt Arial, Helvetica, sans-serif!important;
}

body > table {
    width: 927px!important;
    margin: 0px auto!important;
    text-align: left!important;
    padding: 5px!important;
}

body:after {
    content: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/main_bot01b.jpg')!important;
    display: block!important;
}

body:before {
    content: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/main_top01b.jpg')!important;
    display: block!important;
}

#button {
    width: 186px!important;
    background: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/menu_bkgd01.png') top center repeat-y!important;
    margin-top: 25px!important;
}

#button:after {
    content: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/menu_bot01.png')!important;
    display: block!important;
    margin-bottom: -25px!important;
}

#button:before {
    content: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/menu_top01.png')!important;
    display: block!important;
    position: relative!important;
    margin-top: -25px!important;
}

#button > ul {
    background: none!important;
    margin: -8px 40px 0px 38px!important;
}

#button > ul > li {
    background: none!important;
    margin: 3px 0px!important;
    border: none!important;
}

#button a, #button a:link, #button a:visited, #button a:active {
    color: #000!important;
    text-decoration: none!important;
}

#button a:hover {
    color: #000!important;
    text-decoration: underline!important;
}

.copywrite, .copywrite * {
    font: normal 7pt Arial, Helvetica, sans-serif!important;
    color: #333!important;
    text-decoration: none!important;
}

.copywrite:after {
    text-align: right!important;
}

.headers, h3 {
    color: #600000!important;
    border-bottom: 2px solid #600000!important;
    margin-top: 50px!important;
}

html > body > table > tbody > tr > td > div > img {
    display: none!important;
}

html > body > table > tbody > tr > td > div:before {
    content: url('http://i149.photobucket.com/albums/s62/eq2graphics/hosted/logo01.png')!important;
    margin-bottom: 5px!important;
}

.mainbody {
    max-width: 713px!important;
}

.mainbody img {
    max-width: 675px!important;
}

.mainbody table tbody tr td a img {
    max-width: 200px!important;
}

.mainbody table tbody tr td img {
    max-width: 325px!important;
}

#button li.selected * {
    background: #600000!important;
    color: #fff!important;
    text-decoration: none!important;
    padding: 2px 3px!important;
}

#button li.selected *:hover, #button li.unselected1 *:hover, #button li.unselected2 *:hover, #button li.unselected3 *:hover {
    color: #fff!important;
    background: #606048!important;
    text-decoration: none!important;
    cursor: pointer!important;
}

#button li.selected a img {
    margin: 0px auto!important;
    display: block!important;
    position: relative!important;
    left: -1px!important;
}

#button li.unselected1 * {
    background: none!important;
    padding: 2px 3px!important;
}

#button li.unselected2 a:before, #button li.unselected3 a:before {
    content: "\0000bb\0000a0"!important;
    font-size: 12pt!important;
    line-height: 10pt!important;
}

#button li.unselected2 *, #button li.unselected3 * {
    /*background: #d0d2c4!important;*/
    background: none!important;
    padding: 2px 3px!important;
    margin-left: 10px!important;
}

table {
    margin: 0px!important;
    padding: 0px!important;
}

td, p {
    color: #000!important;
}

td img {
    margin: 10px!important;
    background: #fff!important;
    border: 2px solid #600000!important;
}

/* ***************************************************** */
}
