@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix(http://www.google.com/accounts/ServiceLogin),
url-prefix(https://www.google.com/accounts/ServiceLogin),
url-prefix(https://accounts.google.com/ServiceLogin?service=mail) {


/** Font **/
* {
  font-family: Ubuntu,sans-serif !important
}


/** Background color **/
.wrapper {
/*  background-color: #55688A !important;*/
  background-color: #e5e5e5 !important;
}

/** HIDE STUFF **/

/* Header bar */
.google-header-bar {
  display: none;
}

/* Product Info */
.product-info {
  display: none;
}

/* Footer Bar */
.google-footer-bar {
  display: none;
}


/** Other Adjustments **/

/* adjust width */
.content {
  max-width: none !important;
  min-width: 0 !important;
  width: 335px !important;
  padding: 0 !important;
}

/* Center Login Box */
.sign-in {
  margin: 0 auto !important;
  height: 272px !important;
  with: 335px !important;
  position: absolute !important;
  top: 50%;
  left: 50%;
  margin-left: -167.5px !important;
  margin-top: -132.5px !important;
}

/** Sign-in Box **/
.sign-in {
  box-shadow: 0 2px 4px rgba(0,0,0,.44);
  border-radius: .35em;
  padding: .35em !important;
  background-color: #fff !important;
}

.signin-box {
  padding: 20px 25px 15px !important;
  margin: 0 !important;
  background-color: transparent !important;
  border: none !important;
}


/** Input sprite **/

#Email {
  background: url(data:image/png;base64,) no-repeat 4px 4px #FFF !important;
  padding-left: 32px !important;
}

#Passwd {
  background: url(data:image/png;base64,) no-repeat 4px 4px #FFF !important;
  padding-left: 32px !important;
}


}
