css:
/* Pokemonepisode.org/DBZepisode.org cleanup */
/* made by rctgamer3 */
/* Version 3.5 - 20-03-2013 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pokemonepisode.org"),
domain("dbzepisode.org") {
DIV#page {
  margin: auto !important;
  padding: 0 !important
}

DIV#chatango1,
DIV#chatango2,
DIV#bottom, DIV#footer, 
DIV#top,
DIV#left,
DIV#footerouter,
DIV[style="float: left; margin: 24px 0 0 200px;"],
DIV#header > H1,
DIV#page > DIV#esl + DIV#top + DIV {
  display: none !important
}

DIV#centerouter, DIV#center {
  width: 100% !important
}
DIV#nextprev > UL > LI {
  font-size: 9px !important
}
DIV.eslc {
  margin-right: 0 !important;
  margin-left: 0  !important
}

li {
  margin: auto !important;
  padding: 0px !important
}
DIV.line {
  height: auto !important
}
DIV.epsum {
  padding: 0 !important
}
DIV.epsum > P {
  font-size: 11px !important
}
DIV.centerleft {
  padding: 0px 0px 0px 5px !important
}
DIV.centerleft > DIV {
  background-color: inherit !important
}
DIV.ephome {
  width: 100% !important
}
DIV#centerouter {
  padding: 0px 2px 0px 0px
}
DIV.ephome {
  background-color: inherit !important
}
DIV.centerleft {
  margin-bottom: 5px;
  width: 100%;
}
DIV#es {
  float: left !important;
  margin-left: 250px !important
}
DIV#page {
  padding-bottom: 5px !important
}
/* hide next page button when you're already on the first page */
DIV#nextprevright:not(a) {
  display: none
}
/* hide facebook thiny on top, there are share buttons on eps anyway */
DIV[style="float: left; margin: 24px 0pt 0pt 200px;"] {
  display: none !important
}
DIV[style="margin: 5px 0 0 20px;"] {
  display: none !important
}
DIV.ephome {
  margin-top: -5px !important;
  margin-bottom: -5px !important
}
.c {
  margin-top: 5px !important
}

.vid_current > OBJECT > EMBED {
  width: 705px !important;
  height: 399px !important
}

#es {
  height: 20px !important
}
#es:hover {
  border-top-left-radius:5px;
  border-top-right-radius:5px;
  background: #52c9cb linear-gradient(to bottom, #fff, #52c9cb 20px) !important;
}

/* other sites dead, removed links to them */
.partnersites:nth-child(1), .partnersites:nth-child(2), .partnersites:nth-child(3) {
  display:none !important
}
}

@-moz-document domain("dbzepisode.org") {
#es:hover {
  background: #ff9f42 linear-gradient(to bottom, #fff, #ff9f42 20px) !important;
}
}
