css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.korben.info") {

/* --------------------------------- */
/* PARTIE COMMUNE A TOUTES LES PAGES */
/* --------------------------------- */

body { font-family: Verdana !important; }

/* Modifie le header */
#branding { background-image: none !important;
background-color: #ff9933 !important; }

/* Modifie le bloc de connexion/dÃ©connexion/profil */
#user_navigation { margin-top: -8px !important;
background-color: #FFF !important;
border-color: yellow !important; }
a#sign_in.rounded { background-color: #66aa83 !important; }
#user_navigation .links, #user_navigation .links a { color: grey !important; }
a#user_link, a#user_notifications_link { background-image: none !important;
background-color: #ff9933 !important;
border: 1px outset #ff9933 !important; }
a#user_link:hover, a#user_notifications_link:hover { border: 1px inset #ff9933 !important; }
#user_navigation li a { color: grey !important; }

/* Modifie les onglets de navigation */
#primary_nav { background-image: none !important;
background-color: #FFF !important; }
#primary_nav ul li.left a:hover { color: orange !important; }

/* Modifie la barre de recherche */

/* Modifie les liens de navigation */
ul#breadcrumb.left li { font-size: 14px !important; }
ul#breadcrumb.left li a:hover { color: orange !important; }

/* Modifie la barre de filtrage */
.filter_bar { background-color: #d5dde5 !important; }
.filter_bar label, .filter_bar .clickable { color: black !important; }

/* Modifie la barre de changement de thÃ¨me/langue */
#footer_utilities { background-image: none !important;
background-color: #ff9933 !important; }
#footer_utilities ul.right li { color: #FFF !important; }

/* -------------------------------- */
/* PARTIE CONCERNANT LES CATEGORIES */
/* -------------------------------- */

/* Modifie la barre en haut */
h3.maintitle, caption.maintitle { background-image: none !important;
background-color: #d5d5d3 !important;
font-weight: bold !important;
color: #FFF !important; }
caption.maintitle .desc a { color: #6191b5 !important; }

/* Modifie la barre "Sujet, CommencÃ© par, Stat..." */
tr.header { background-image: url(http://m.bestofmedia.com/i/tomsguide/design/bg-contentGeneric.gif) !important;
background-repeat: repeat-x !important;
background-color: transparent !important; }

/* Modifie les couleurs de fond */
tr.row1 td { background-color: #FFFFFF !important; }
tr.row2 td { background-color: #fef9f0 !important; }
tr.row1 td:hover, tr.row2 td:hover { background-color: #eceffe !important; }

/* Augmente la taille des sous-titres de sujets */
.desc { font-size: 11px !important; }

/* Supprime la bordure en bas du tableau des sujets */
#forum_table, table.ipb_table, h3.collapsed { border-bottom: none !important; }

/* ---------------------------- */
/* PARTIE CONCERNANT LES SUJETS */
/* ---------------------------- */

/* Modifie les boutons "Suivre, rÃ©pondre, nouveau sujet" */
ul.topic_buttons li, ul.topic_buttons li a, ul.topic_buttons li.closed span, ul.topic_buttons li.disabled span { background-image: none !important; }
ul.topic_buttons li a { padding-left: 10px !important;
background-color: orange !important;
border: 2px outset orange !important; }
ul.topic_buttons li a:hover { border: 2px inset orange !important; }
ul.topic_buttons li.closed span { padding-left: 10px !important;
padding-bottom: 5px !important;
background-color: #920d0d !important;
border: 2px outset #920d0d !important; }
ul.topic_buttons li.disabled span { padding-left: 10px !important;
background-color: #e3e3e3 !important;
border: 2px outset #e3e3e3 !important; }

/* Modifie la barre en haut de chaque sujet */
h2.maintitle { background-image: none !important;
background-color: #d5d5d3 !important; }
h2.maintitle .main_topic_title { font-weight: bold !important;
color: #FFF !important; }
h2.maintitle .main_topic_title .desc { color: #6191b5 !important; }

/* Modifie la barre en haut de chaque post */
.post_wrap h3 { background-image: url(http://m.bestofmedia.com/i/tomsguide/design/bg-contentGeneric.gif) !important;
background-repeat: repeat-x !important;
background-color: transparent !important; }

/* Modifie le "PostÃ© le ... Ã  ... */
p.posted_info { width: 155px !important;
padding: 2px !important;
margin-left: -9px !important;
margin-bottom: 10px !important;
border-right: 1px dotted #6191b5 !important;
border-bottom: 1px dotted #6191b5 !important; }

/* Modifie le cadre de profil */
.author_info { margin-top: 17px !important;
border-right: 1px dotted #6191b5 !important; }

/* Modifie la case de vote */
.rep_bar { background-color: white !important; }

/* Modifie la signature */
.signature { margin-left: -9px !important;
border-top: 1px dotted #6191b5 !important; }

/* Modifie la barre en bas de chaque post */
ul.post_controls { background-color: transparent !important; }
ul.post_controls li, ul.post_controls li a { background-image: none !important; }
ul.post_controls li a { padding-left: 10px !important;
background-color: white !important;
border: 2px outset white !important; }
ul.post_controls li a:hover { border: 2px inset white !important; }

/* Supprime la bordure bleue en bas de chaque post */
.post_block { margin-bottom: 10px !important;
border-bottom-width: 0px !important; }

/* ----------------------------------- */
/* PARTIE CONCERNANT LES OPTIONS PERSO */
/* ----------------------------------- */

/* Modifie la barre "ParamÃ¨tres / Profil / Forums" */
ul.tab_bar.no_title { height: 28px !important;
padding: 10px 0 0 10px !important;
background-image: none !important; }
ul.tab_bar.no_title li.active { background-color: #ff9933 !important; }

/* Modifie le menu sur la gauche */
#usercp_content { border-left-color: #ff9933 !important; }
ul#usercp_menu { border-right-color: #ff9933 !important; }
ul#usercp_menu li.active { background-color: #ff9933 !important; }

/* Modifie la police de la zone de saisie du texte */
.input_rte { font-family: Verdana !important;
font-size: 12px !important; }

/* -------------------------------- */
/* PARTIE CONCERNANT D'AUTRES PAGES */
/* -------------------------------- */

/* Colore le fond du bloc "DerniÃ¨re news: " */
#latest_news { background-color: #f9f1e3 !important;
border: 2px #f9f1e3 outset !important; }

/* Colore les onglets sur les blocs Ã  droite de l'accueil */
.tab_bar li.active { background-color: #ff9933 !important; }
.tab_bar.no_title.mini { border-bottom-color: #ff9933 !important; }

/* Colore les onglets sur la liste des membres */
.tab_filters { background-image: none !important;
background-color: #ff9933 !important; }

}
