css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.metafilter.com") 
{


body {background-color: ghostwhite !important;
background-image: url(data:image/png;base64,) !important;
color: black !important;}


.tags, div#menu
{background-color: #ededff !important;
border: 4px solid #d7d7f0 !important;
-moz-border-radius: 15px 15px 15px 15px !important;}

/*home page*/
div#menufooter
{background-color: #ededff !important;
border: none !important;}

/* Headers */
.posttitle {background-color: #d3d3f1 !important;
-moz-border-radius: 15px 15px 15px 15px !important;
padding: 8px !important;
width: 80% !important;}

.monthday, h2 {background-color: #d3d3f1 !important;
-moz-border-radius: 15px 15px 15px 15px !important;
padding: 8px !important;
width: 30% !important;}
/**/

div#bottomline{
  background-image: none !important;
background-color: transparent !important;
}


/* 7180B7 */
a:link {color: #6677b7 !important;}
a:visited {color: #663399 !important;}

#maintab li.selected a
{background-color: #ebebff !important;
-moz-border-radius: 11px 11px 0px 0px !important;}
#maintab li a
{background-color: #a5b1dd !important;
-moz-border-radius: 11px 11px 0px 0px !important;}
#maintab li a:hover
{background-color: #d4d4f0 !important;
-moz-border-radius: 11px 11px 0px 0px !important;}

/*Search Buttons*/
.button
{background: #a5b1dd !important;}


#yellowbar {font-size: 60% !important;}
.posttitle {color: black !important;}
.smallcopy {color: #5F6475 !important;}



p[style="padding: 3px; margin-top: 8px; text-align: center; font-size: 10px; width: 115px;"],
iframe,
div#menu div[align="center"],
p[style="padding: 3px; margin-top: 4px; text-align: center; font-size: 9px; width: 160px;"]
{display: none !important;}


#footer {background-image: url(data:image/png;base64,) !important;
}

#topline
 {background-image: url(data:image/png;base64,) !important;
}



}
