css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.sigames.com") {
.postbody{background-color:#ECF0F3!important;}
.postfoot{border-bottom: 1px solid #61a7dc !important;}

html {background:#dddddd!important;}

h2.title {font-size:9px !important;}

blockquote {font-size:12px !important; font-family:"verdana" !important;}

.body_wrapper {background-color: #e6eaee!important;}

a.threadtitle_unread:link {color:#85262f!important;}
a.threadtitle_unread:visited{color:#003399!important;}
.title {color:#85262f!important;}
.titleline a:link {color:#85262f!important}
.titleline a:visited{color:#85262f!important}

a.username:link {color:#85262f!important}
a.username:visited{color:#85262f!important}

span.selected {background-color:#dfdfff}

.qrcontainer {background-color:#e6eaee!important;}

.wysiwyg_block {background-color:#dddddd!important;}

.editor{background-color:#dddddd!important;}

.actionbuttons{background-color:#dddddd!important;}

.blockrow{background-color:#dddddd!important;}

textarea {background-color: white!important;}
}
