css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#extensionsManager > vbox, #sidebar-title, #downloadManager
{
-moz-appearance: -moz-win-glass !important;
background: transparent !important;
}

#downloadView {
border: none!important;
opacity: 0.8 !important;
text-shadow: 
0px 0px 13px white,
0px 0px 13px white,
0px 0px 13px white,
0px 0px 13px white !important;
color: black !important;
background: transparent  !important;
background-repeat:no-repeat !important;
background-position: center !important;
}

#downloadView:hover
{
opacity: 1 !important;
}

#downloadView > richlistitem {
color: black !important;
border-color: transparent !important;
border: none !important;
outline: none !important;
}

#downloadView > richlistitem[selected=true] {
background: rgba(0,0,0,.1) !important;
-moz-border-radius: 7px !important;

}

#downloadView > richlistitem:hover, #downloadView > richlistitem[selected=true]:hover
{
-moz-border-radius: 7px !important;
background:  rgba(0,0,0,.3) !important;
}

#downloadManager scrollbar{
-moz-appearance: none !important;
background:transparent !important;
}


#downloadManager thumb:active, scrollbarbutton:active {
opacity: 1 !important;
}


#downloadManager thumb, #downloadManager scrollbarbutton, #downloadManager thumb:hover, #downloadManager scrollbarbutton:hover, #searchbox
{
opacity: .6 !important;
}

#searchbox{
margin-top: 12px !important;
width:180px!important;
} 

#searchbox:hover{
opacity:1!important;
}

#clearListButton{
margin-top: 10px !important;
margin-bottom: 6px !important;
width:140px!important;
opacity:0.6!important;
height: 28px!important;
width: 100px !important;
}

#clearListButton[disabled="true"]{
display: none !important;
}

#clearListButton:hover{
opacity:1!important;
-moz-border-radius:10px;
text-shadow: 
black 0px 0px 23px !important;
}
