css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#top {
 display: inline-block!important; 
 width: 100%!important;
 padding-top: 29px!important;
 }

#top-collect .c, a[href$="https://www.deviantart.com/checkout/?mx=offer"], #loginbar-group {
 display: none!important;
}

.bubbleprefix {
 display: inline-block!important;
 left: 0px!important;
 width: 100%!important;
 border: 0px!important;
}

.bubbleprefix .tl {
 border-left: 19px solid #4E6257!important;
}

.bubbleprefix .tr {
 border-right: 19px solid #4E6257!important;
}

span.logout a {
 background-position: 100% -169px!important;
}

span.logout a:hover {
 background-position: 100% -196px!important;
}

#logindock {
 margin-left: 0px!important;
 margin-right: 0px!important;
}

#top-collect #lubber, #lub {
 left: 0px!important;
 right: 0px!important;
 margin-right:1px!important;
}

#lubber i {
 display: none;
}

#top-collect .ch-ctrl {
 position: fixed;
 border-left: 0px!important;
 border-right: 0px!important;
 width: 100%;
 top:0px!important;
 border-top: 0px!important;
}

#top-collect .ch-ctrl .text {
 padding-left: 15px!important;
}

.glink {
 margin-top: -1px!important;
}

#form-login .inputter {
 top: 1px!important;
}

#form-login .login span a {
 top: -1px!important;
 height: 27px!important;
}

#form-login .text a.side {
 top: 0px!important;
}

#top-collect a[href$="http://my.deviantart.com/services/subscription/?mx=simplify"] {
 display: none;
}

a[title][href*="messages"]:after{
 content: " ("attr(title)") "
}

.mczone-empty {
 font-style: italic;
}

.popup2-click-friends {
 position:fixed!important;
 top: 28px!important;
}

.popup2-shadow-click-friends {
 position: fixed!important;
 top: 15px!important;
}

.blues-bar-ctrl {
 z-index: 30!important;
}

.popup2-shadow {
 z-index: 20!important;
}

.gr-top, .gr-box, .mczone {
z-index: 10!important;
}

}
