@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://carnage.bungie.org') {

div#glob_wrapbbs div, div#glob_wrapbbs span, div#glob_wrapbbs table, div#glob_wrapbbs table th, 

div#glob_wrapbbs table td
{ border: #344152 !important; }

div#d4, div#misc_dbcheck table th, div#adm_wrap table th, div#msg_form table th, div#misc_setpref table th, 

div#misc_msgform table th, div#prof_form table th, div#misc_topstat table th, div#misc_bemail table th, 

td.misc_msgform_b
{ background-color:#506987 !important; }

div.adm_navbar, div.glob_navbar, div#ind_paginate
{ background-color:#344152 !important; }

div.msg_headln, div.msg_delete, div.msg_gbnavbar, div#prof_poster
{ background-color:#405773 !important; }

div.msg_userln
{ background-color:#506987 !important; }

span.ind_yourhere, div.msg_prev, div.msg_text, div.msg_link
{ background-color:#344152 !important; }

div#ind_msglist a:visited, div#msg_respons a:visited
{ color:#A4D3EE !important; }

body
{ background-color:#344152 !important; }

body, table
{ color:AntiqueWhite; }

a
{ color:#CBB189 !important; }

a:visited
{ color:#A4D3EE !important;}

a:hover { color:#A4D3EE !important;}

input, textarea, select
{
background-color:#9FB6CD !important;
border:thin !important;
border-collapse:collapse !important;
border-spacing:0 !important;
}

div#glob_header
{ background-color:#506987 !important; }

div#glob_footerbbs p
{ color:gray !important; }

div.msg_text blockquote
{ color:#DEB37B !important; }

div#msg_respons
{ color:AntiqueWhite !important; }

.g
{ background-color:#506987 !important; }

body > table tr:first-child > td:first-child + td + td,
body > table tr:first-child + tr + tr + tr + tr + tr + tr,
body > table tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr,
body > table tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr 
{
visibility: collapse;
}

#forumnews
{
max-width:270px !important;
top:200px !important;
}

.main
{
position:absolute !important;
top: 0px !important;
visibility:visible !important;
}

body > table tr:first-child + tr > td:first-child
{ background-color:white !important; }

body > div#glob_wrapbbs > div#misc_msgform > form > table > tbody > tr > td > div > table
{ visibility: collapse !important; }

}
