css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dirty.ru") {

div.menu
{
	padding: 5px 0px 5px 0px !important;
}
div.post
{
	margin-bottom: 30px !important;
}
div.post_comments_page
{
	padding-top: 20px !important;
	margin-right: -300px !important;
}
div.posts_threshold
{
	min-height: 80px !important;
	margin-right: 300px !important;
}
div.h-tags_comments_page
{
	width: 300px !important;
}


div.comment
{
	margin-bottom: 10px !important;
}
div.comments_header
{
	margin-bottom: 25px !important;
}

div.header
{
	min-height: 60px !important;
}

div.header_logout
{
	text-align: left !important;
}

div.header_tagline
{
	margin: 10px 10px 0px 80px !important;
	border: none !important;
}

div.header_tagline_inner
{
	padding: 0px 0px 0px 0px !important;
}

div.inbox_header
{
	padding: 20px 20px 20px 42px !important;
	background: #FAFAFA !important;
}

/* Remove frame around welcome text */
div.header_tagline_arrow
{
	display:none !important;
}

div.header_tagline_arrow_i
{
	display:none !important;
}

div.header_nav
{
	padding-top: 0px !important;
}

div.header_nav_menu
{
	margin-bottom: 0px !important;
}

/* old style birg for logo */
a#js-logo
{
	left: 0px !important;
	top: 0px !important;
	background: url(http://pit.dirty.ru/dirty/1/2010/07/08/28284-032157-648c8cf52465a719532dac687a48f8c6.png) no-repeat 50% 50% !important;
}

div.comments_header_threshhold_inner
{
	padding: 12px 20px 1px 43px !important;
}

div.comments_header_controls_inner
{
	margin: 12px 100px 0px 0px !important;
}

a#js-comments_header_new_selector
{
	top: 5px !important;
}

/* Color my comments #FFF8E6 <- background color */
div.mine
{
	background:#FFF8E6 !important;
}

/* A window with horse */
div.b-ads
{
	display: none !important;
}

/* Tweak links in heared My things / Favorites / Inboxes / New post */
div.header_my_things
{
	margin: 10px 340px 10px 0px !important;
}

a.header_inbox_link
{
	margin-top: 2px !important;
	margin-right: 10px !important;
}

a.header_favs_link
{
	margin-right: 10px !important;
}

a.header_my_things_link
{
	background: none !important; margin-right: 10px !important; 
	padding-left: 0px !important;
}

a.header_write_link
{
	background: none !important; 
	margin-right: 10px !important; 
	margin-right: 0px !important;
	padding-left: 0px !important;
}


/* inbox tweaks  */
a.dashed
{
	margin-left: 20px !important; 
}

/* comments indent tweak and fix for indent_18 */
.comments_indent_holder .comment { margin-left:355px !important;}
.comments_indent_holder .indent_0 { margin-left:40px !important;}
